// src/components/FeedbackWall/FeedbackWall.js (將角色搜尋移入 Collapse)

import React, { useState, useEffect, useMemo } from 'react';
import { Container, Row, Col, Card, Alert, Badge, InputGroup, Form, Button, Collapse } from 'react-bootstrap';
import FeedbackChart from './FeedbackChart';
import './FeedbackWall.css';

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';

function FeedbackWall() {
  const [originalFeedbackHistory, setOriginalFeedbackHistory] = useState([]);
  const [filteredFeedbackHistory, setFilteredFeedbackHistory] = useState([]);
  const [characterSearch, setCharacterSearch] = useState('');
  const [goalSearch, setGoalSearch] = useState('');
  const [showFilters, setShowFilters] = useState(true); // 篩選區域默認展開

  // --- 加載和篩選邏輯保持不變 ---
  useEffect(() => { /* ... 加載 ... */ const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY); let parsedFeedback = []; if (savedFeedback) { try { parsedFeedback = JSON.parse(savedFeedback); if (!Array.isArray(parsedFeedback)) parsedFeedback = []; parsedFeedback = parsedFeedback .filter(fb => typeof fb.timestamp === 'number') .sort((a, b) => b.timestamp - a.timestamp); } catch (e) { console.error("Failed to parse feedback history", e); } } setOriginalFeedbackHistory(parsedFeedback); }, []);
  useEffect(() => { /* ... 篩選 ... */ let result = [...originalFeedbackHistory]; if (characterSearch.trim()) { const lowerCaseSearch = characterSearch.trim().toLowerCase(); result = result.filter(fb => fb.characterName?.toLowerCase().includes(lowerCaseSearch) ); } if (goalSearch.trim()) { const lowerCaseGoal = goalSearch.trim().toLowerCase(); result = result.filter(fb => fb.goal?.toLowerCase().includes(lowerCaseGoal) ); } setFilteredFeedbackHistory(result); }, [originalFeedbackHistory, characterSearch, goalSearch]);

  // 清除篩選條件 (確保也清除 characterSearch)
  const clearFilters = () => {
      setCharacterSearch(''); // <<<--- 添加清除角色搜尋
      setGoalSearch('');
  }

  // --- 圖表數據準備 (保持不變) ---
  const recentFilteredFeedback = filteredFeedbackHistory.slice(0, 5).reverse();
  const chartLabels = recentFilteredFeedback.map((fb, index, arr) => { /* ... */ const date = new Date(fb.timestamp); const dateString = date.toLocaleDateString('zh-TW', { month: 'numeric', day: 'numeric' }); const timeString = date.toLocaleTimeString('zh-TW', { hour: '2-digit', minute: '2-digit', hour12: false }); const prevDateString = index > 0 ? new Date(arr[index - 1].timestamp).toLocaleDateString('zh-TW', { month: 'numeric', day: 'numeric' }) : null; return dateString === prevDateString ? `${dateString} ${timeString}` : dateString; });
  const getScore = (score) => (score === null || score === undefined ? 0 : score);
  const chartDataSets = [ /* ... */ { label: '清晰度', data: recentFilteredFeedback.map(fb => getScore(fb.scores?.clarity)), backgroundColor: 'rgba(54, 162, 235, 0.6)', borderColor: 'rgba(54, 162, 235, 1)', borderWidth: 1 }, { label: '同理心', data: recentFilteredFeedback.map(fb => getScore(fb.scores?.empathy)), backgroundColor: 'rgba(255, 206, 86, 0.6)', borderColor: 'rgba(255, 206, 86, 1)', borderWidth: 1 }, { label: '自信', data: recentFilteredFeedback.map(fb => getScore(fb.scores?.confidence)), backgroundColor: 'rgba(75, 192, 192, 0.6)', borderColor: 'rgba(75, 192, 192, 1)', borderWidth: 1 }, { label: '適當性', data: recentFilteredFeedback.map(fb => getScore(fb.scores?.appropriateness)), backgroundColor: 'rgba(153, 102, 255, 0.6)', borderColor: 'rgba(153, 102, 255, 1)', borderWidth: 1 }, { label: '目標達成', data: recentFilteredFeedback.map(fb => getScore(fb.scores?.goalAchievement)), backgroundColor: 'rgba(255, 99, 132, 0.6)', borderColor: 'rgba(255, 99, 132, 1)', borderWidth: 1 }, ];

  return (
    <Container fluid className="feedback-wall-container p-0 d-flex flex-column">

      {/* --- 固定的頭部區域 (只有標題和描述) --- */}
      <div className="feedback-wall-header">
        <h2 className="mb-2">回饋牆</h2>
        <p className="text-muted mb-0">這裡彙總了你每次請求 AI 回饋的結果，觀察你的進步趨勢吧！</p>
        {/* --- 角色搜尋框移到下方 --- */}
      </div>

      {/* --- 篩選控制項區域 (包含角色和目標搜尋，可收起/展開) --- */}
      <div className="feedback-filters-container">
        <div className="d-flex justify-content-between align-items-center mb-2">
          <h6 className="mb-0 text-muted small">搜尋與篩選</h6>
          <Button variant="link" size="sm" onClick={() => setShowFilters(!showFilters)} aria-controls="collapse-feedback-filters" aria-expanded={showFilters} className="p-0 text-secondary">
            {showFilters ? '收起' : '展開'} <i className={`bi bi-chevron-${showFilters ? 'up' : 'down'}`}></i>
          </Button>
        </div>
        <Collapse in={showFilters}>
          <div id="collapse-feedback-filters">
            {/* --- 使用 Row 和 Col 佈局篩選器 --- */}
            <Row className="g-2"> {/* g-2 添加間隙 */}
              <Col md={6}> {/* 角色搜尋佔一半寬度 */}
                <InputGroup size="sm">
                  <InputGroup.Text><i className="bi bi-person"></i></InputGroup.Text> {/* 換個圖標 */}
                  <Form.Control
                    type="text"
                    placeholder="搜尋角色名稱..."
                    value={characterSearch}
                    onChange={(e) => setCharacterSearch(e.target.value)}
                  />
                </InputGroup>
              </Col>
              <Col md={6}> {/* 目標搜尋佔另一半寬度 */}
                <InputGroup size="sm">
                  <InputGroup.Text><i className="bi bi-chat-dots"></i></InputGroup.Text> {/* 換個圖標 */}
                  <Form.Control
                    type="text"
                    placeholder="搜尋對話目標關鍵字..."
                    value={goalSearch}
                    onChange={(e) => setGoalSearch(e.target.value)}
                  />
                </InputGroup>
              </Col>
            </Row>
             {/* TODO: 在此處添加其他篩選器，例如分數範圍 */}
             {/* 清除按鈕放在下方 */}
             {(characterSearch || goalSearch) && ( // 只有在有篩選條件時才顯示清除按鈕
                <Button variant="outline-secondary" size="sm" onClick={clearFilters} className="mt-2 w-100">
                   <i className="bi bi-x-lg"></i> 清除所有篩選
                </Button>
              )}
          </div>
        </Collapse>
      </div>
      {/* --- 結束篩選控制項區域 --- */}

      {/* --- 可滾動的內容區域 (保持不變) --- */}
      <div className="feedback-wall-body">
         {/* ... (圖表和歷史記錄列表) ... */}
      </div>
    </Container>
  );
}

export default FeedbackWall;
