// src/components/CharacterHall/CharacterEvalModal.js (添加刪除功能)

// --- 修改這裡：導入 useCallback ---
import React, { useState, useEffect, useMemo, useCallback } from 'react';
// ... 其他導入 ...
import { Modal, Button, Alert, ListGroup, Badge, Collapse, Form, InputGroup, Row, Col } from 'react-bootstrap';
import ChartModal from '../FeedbackWall/ChartModal';
import AverageRadarChart from '../FeedbackWall/AverageRadarChart';
import './CharacterHall.css'; // 確保導入 CSS

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';
const ALL_METRICS = [ { key: 'clarity', label: '清晰度' }, { key: 'empathy', label: '同理心' }, { key: 'confidence', label: '自信' }, { key: 'appropriateness', label: '適當性' }, { key: 'goalAchievement', label: '目標達成' }, ];
const getScore = (score) => (score === null || score === undefined ? 0 : score);

// --- 移除 onDeleteFeedback prop，因為 Modal 自己處理 ---
function CharacterEvalModal({ show, onHide, character }) {
  // --- 使用一個狀態同時保存原始和篩選後的數據 ---
  // originalCharacterFeedback 不再需要，直接從 localStorage 加載並篩選
  const [filteredCharacterFeedback, setFilteredCharacterFeedback] = useState([]);
  const [goalSearchModal, setGoalSearchModal] = useState('');
  const [showEvalFilters, setShowEvalFilters] = useState(false);
  const [showEvalChartModal, setShowEvalChartModal] = useState(false);
  const [showEvalRadarModal, setShowEvalRadarModal] = useState(false);

  // --- 加載和篩選數據 ---
  const loadAndFilterFeedback = useCallback(() => {
    if (show && character) {
      console.log(`EvalModal - Loading feedback for character: ${character.name} (ID: ${character.id})`);
      const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
      let allFeedback = [];
      if (savedFeedback) {
        try {
          allFeedback = JSON.parse(savedFeedback);
          if (!Array.isArray(allFeedback)) allFeedback = [];
        } catch (e) { console.error("EvalModal - Failed to parse feedback history", e); allFeedback = []; }
      }

      // 篩選屬於當前角色的回饋
      let characterFeedback = allFeedback
        .filter(fb => fb.characterId === character.id && typeof fb.timestamp === 'number')
        .sort((a, b) => b.timestamp - a.timestamp); // 按時間降序存儲

      // 根據目標關鍵字進一步篩選
      if (goalSearchModal.trim()) {
        const lowerCaseGoal = goalSearchModal.trim().toLowerCase();
        characterFeedback = characterFeedback.filter(fb =>
            fb.goal?.toLowerCase().includes(lowerCaseGoal)
        );
        console.log(`EvalModal - Filtered by goal "${goalSearchModal}", ${characterFeedback.length} results.`);
      } else {
        console.log("EvalModal - Goal search cleared.");
      }

      console.log(`EvalModal - Displaying ${characterFeedback.length} records for this character.`);
      setFilteredCharacterFeedback(characterFeedback); // 更新顯示的數據

    } else {
      setFilteredCharacterFeedback([]); // 清空
    }
  }, [show, character, goalSearchModal]); // 依賴這些變化

  // 組件顯示或篩選詞變化時，重新加載和篩選
  useEffect(() => {
    loadAndFilterFeedback();
  }, [loadAndFilterFeedback]); // 使用 useCallback 包裹的函數作為依賴


  // --- 刪除回饋處理函數 ---
  const handleDeleteFeedbackInModal = (timestampToDelete) => {
    const feedbackToDelete = filteredCharacterFeedback.find(fb => fb.timestamp === timestampToDelete);
    if (feedbackToDelete && window.confirm(`確定要刪除這筆 ${new Date(timestampToDelete).toLocaleString('zh-TW')} 的回饋記錄嗎？\n目標：${feedbackToDelete.goal}`)) {
      try {
        // 1. 從 localStorage 讀取 *所有* 回饋
        const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
        let allFeedback = [];
        if (savedFeedback) {
          allFeedback = JSON.parse(savedFeedback);
          if (!Array.isArray(allFeedback)) allFeedback = [];
        }

        // 2. 過濾掉要刪除的記錄
        const updatedAllHistory = allFeedback.filter(fb => fb.timestamp !== timestampToDelete);

        // 3. 將更新後的 *所有* 回饋寫回 localStorage
        localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify(updatedAllHistory));
        console.log(`EvalModal - Deleted feedback with timestamp: ${timestampToDelete}`);

        // 4. 重新加載和篩選當前 Modal 顯示的數據
        loadAndFilterFeedback(); // 調用加載函數來更新界面

      } catch (e) {
        console.error("Error deleting feedback from localStorage", e);
        alert("刪除回饋時發生錯誤。");
      }
    }
  }

  // --- 計算平均分數 (基於篩選後的數據) ---
  const averageScores = useMemo(() => { /* ... (保持不變) ... */ const scoreSums = { clarity: 0, empathy: 0, confidence: 0, appropriateness: 0, goalAchievement: 0 }; const scoreCounts = { clarity: 0, empathy: 0, confidence: 0, appropriateness: 0, goalAchievement: 0 }; filteredCharacterFeedback.forEach(fb => { if (fb.scores) { ALL_METRICS.forEach(metric => { const score = fb.scores[metric.key]; if (score !== null && score !== undefined) { scoreSums[metric.key] += score; scoreCounts[metric.key]++; } }); } }); const averages = {}; ALL_METRICS.forEach(metric => { averages[metric.key] = scoreCounts[metric.key] > 0 ? scoreSums[metric.key] / scoreCounts[metric.key] : 0; }); return averages; }, [filteredCharacterFeedback]);

  // 清除 Modal 內的篩選
  const clearModalFilters = () => { setGoalSearchModal(''); };

  return (
    <Modal show={show} onHide={onHide} centered size="lg" dialogClassName="character-eval-modal-dialog">
      <Modal.Header closeButton> <Modal.Title>與 "{character?.name}" 的對話評估</Modal.Title> </Modal.Header>
      <Modal.Body className="d-flex flex-column p-0 character-eval-modal-body">
         {/* 可收起的篩選區域 */}
         <div className="character-eval-filters bg-light p-3 border-bottom">
           <div className="d-flex justify-content-between align-items-center mb-2"> <h6 className="mb-0 text-muted small">搜尋對話目標</h6> <Button variant="link" size="sm" onClick={() => setShowEvalFilters(!showEvalFilters)} aria-controls="collapse-eval-filters" aria-expanded={showEvalFilters} className="p-0 text-secondary"> {showEvalFilters ? '收起' : '展開'} <i className={`bi bi-chevron-${showEvalFilters ? 'up' : 'down'}`}></i> </Button> </div>
           <Collapse in={showEvalFilters}>
             <div id="collapse-eval-filters">
                <InputGroup size="sm" className="mb-2"> <InputGroup.Text><i className="bi bi-search"></i></InputGroup.Text> <Form.Control type="text" placeholder="搜尋此角色的對話目標..." value={goalSearchModal} onChange={(e) => setGoalSearchModal(e.target.value)} /> </InputGroup>
                 {goalSearchModal && ( <Button variant="outline-secondary" size="sm" onClick={clearModalFilters} className="w-100"> 清除目標搜尋 </Button> )}
             </div>
           </Collapse>
         </div>
        {/* 可滾動的內容區域 */}
        <div className="character-eval-content flex-grow-1 overflow-auto p-3">
          {/* --- 檢查是否有數據 (基於 filtered) --- */}
          {filteredCharacterFeedback.length > 0 ? (
            <>
              {/* 圖表按鈕 */}
              <div className="text-end mb-3 d-flex justify-content-end">
                   <Button variant="outline-success" size="sm" onClick={() => setShowEvalRadarModal(true)} disabled={filteredCharacterFeedback.length === 0} className="me-2"> <i className="bi bi-bullseye me-1"></i> 查看平均雷達圖 </Button>
                   <Button variant="outline-primary" size="sm" onClick={() => setShowEvalChartModal(true)} disabled={filteredCharacterFeedback.length === 0}> <i className="bi bi-graph-up me-1"></i> 查看趨勢圖表 </Button>
               </div>
              {/* 歷史記錄列表 */}
              <h5 className="mb-3">詳細回饋記錄 ({filteredCharacterFeedback.length} 筆)</h5>
              <ListGroup variant="flush">
                {filteredCharacterFeedback.map(fb => (
                  <ListGroup.Item key={fb.timestamp} className="px-0 d-flex justify-content-between align-items-start">
                    {/* 左側內容 */}
                    <div className="flex-grow-1 me-2">
                      <div className="d-flex justify-content-between align-items-center mb-2"> <small className="text-muted">{new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })}</small> <Badge bg="light" text="dark">目標: {fb.goal || '未指定'}</Badge> </div> <h6>AI 總結:</h6> <p style={{ whiteSpace: 'pre-wrap', fontSize: '0.95em' }}>{fb.summary || '-'}</p>
                      {fb.scores && (
                        <>
                          <p className="small mb-1">評分:</p>
                          <ul className="list-inline small mb-0">
                            {ALL_METRICS.map(metric => { const scoreValue = fb.scores[metric.key]; const displayValue = (scoreValue === null || scoreValue === undefined) ? 'N/A' : getScore(scoreValue); const badgeBg = displayValue === 'N/A' ? 'light' : (getScore(scoreValue) >= 60 ? "success" : "warning"); return ( <li key={metric.key} className="list-inline-item me-3"> {metric.label}: <Badge pill bg={badgeBg} text={displayValue === 'N/A' ? 'dark' : undefined}> {displayValue} </Badge> </li> ); })}
                          </ul>
                        </>
                      )}
                    </div>
                    {/* --- 右側刪除按鈕 --- */}
                    <Button
                       variant="outline-danger"
                       size="sm"
                       className="delete-feedback-btn p-0 flex-shrink-0"
                       onClick={() => handleDeleteFeedbackInModal(fb.timestamp)} // <<<--- 調用 Modal 內部的刪除函數
                       title="刪除此筆回饋"
                       style={{ lineHeight: 1, width: '24px', height: '24px' }}
                    >
                       <i className="bi bi-x"></i>
                    </Button>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </>
          ) : (
             // 根據是否有原始數據和篩選條件顯示不同提示
             originalCharacterFeedback.length > 0 ?
             <Alert variant="warning">找不到符合目標關鍵字 "{goalSearchModal}" 的記錄。</Alert>
             : <Alert variant="info">還沒有與此角色的對話評估記錄。</Alert>
          )}
        </div>
      </Modal.Body>
      <Modal.Footer> <Button variant="secondary" onClick={onHide}> 關閉 </Button> </Modal.Footer>
       {/* 圖表 Modals */}
       <ChartModal show={showEvalChartModal} onHide={() => setShowEvalChartModal(false)} feedbackData={filteredCharacterFeedback}/>
       <Modal show={showEvalRadarModal} onHide={() => setShowEvalRadarModal(false)} centered>
         <Modal.Header closeButton> <Modal.Title>"{character?.name}" 平均表現雷達圖</Modal.Title> </Modal.Header>
         <Modal.Body> {filteredCharacterFeedback.length > 0 ? ( <AverageRadarChart averageScores={averageScores} /> ) : ( <Alert variant="info">沒有數據可計算平均分數。</Alert> )} <p className="text-muted small mt-3"> 注意：此雷達圖顯示的是當前目標篩選條件下，與此角色互動記錄的平均分數。 </p> </Modal.Body>
         <Modal.Footer> <Button variant="secondary" onClick={() => setShowEvalRadarModal(false)}> 關閉 </Button> </Modal.Footer>
       </Modal>
    </Modal>
  );
}
export default CharacterEvalModal;
