// src/components/CharacterHall/CharacterEvalModal.js (添加目標搜尋)

import React, { useState, useEffect } from 'react';
// --- 導入 Form, InputGroup ---
import { Modal, Button, Alert, ListGroup, Badge, Form, InputGroup } from 'react-bootstrap';
import FeedbackChart from '../FeedbackWall/FeedbackChart';

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';

function CharacterEvalModal({ show, onHide, character }) {
  const [originalCharacterFeedback, setOriginalCharacterFeedback] = useState([]); // 原始數據
  const [filteredCharacterFeedback, setFilteredCharacterFeedback] = useState([]); // 篩選後數據
  const [goalSearchModal, setGoalSearchModal] = useState(''); // <<<--- Modal 內部目標搜尋狀態

  useEffect(() => {
    if (show && character) {
      const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
      let allFeedback = [];
      if (savedFeedback) {
        try { allFeedback = JSON.parse(savedFeedback); if (!Array.isArray(allFeedback)) allFeedback = []; }
        catch (e) { console.error("Parsing feedback failed", e); }
      }
      const filtered = allFeedback
        .filter(fb => fb.characterId === character.id && typeof fb.timestamp === 'number')
        .sort((a, b) => b.timestamp - a.timestamp); // 初始按時間降序
      setOriginalCharacterFeedback(filtered); // 保存原始數據
      setFilteredCharacterFeedback(filtered); // 初始顯示所有數據
      setGoalSearchModal(''); // 清空搜尋條件
    } else {
      setOriginalCharacterFeedback([]);
      setFilteredCharacterFeedback([]);
      setGoalSearchModal('');
    }
  }, [show, character]);

  // --- 添加 Modal 內部的篩選邏輯 ---
  useEffect(() => {
      if (!show) return; // Modal 關閉時不執行篩選

      let result = [...originalCharacterFeedback];

      if (goalSearchModal.trim()) {
          const lowerCaseGoal = goalSearchModal.trim().toLowerCase();
          result = result.filter(fb =>
              fb.goal?.toLowerCase().includes(lowerCaseGoal)
          );
      }

      setFilteredCharacterFeedback(result);

  }, [originalCharacterFeedback, goalSearchModal, show]); // 依賴原始數據和 Modal 內部搜尋條件

  // --- 圖表數據使用篩選後的數據 ---
  const recentEvalFeedback = filteredCharacterFeedback.slice(0, 5).reverse(); // 取篩選後的最近 5 筆反轉
  const chartLabels = recentEvalFeedback.map((fb) => new Date(fb.timestamp).toLocaleDateString('zh-TW', { month: 'numeric', day: 'numeric' }));
  const getScore = (score) => (score === null || score === undefined ? 0 : score);
  const chartDataSets = [ /* ... (保持不變) ... */ { label: '清晰度', data: recentEvalFeedback.map(fb => getScore(fb.scores?.clarity)), backgroundColor: 'rgba(54, 162, 235, 0.6)' }, { label: '同理心', data: recentEvalFeedback.map(fb => getScore(fb.scores?.empathy)), backgroundColor: 'rgba(255, 206, 86, 0.6)' }, { label: '自信', data: recentEvalFeedback.map(fb => getScore(fb.scores?.confidence)), backgroundColor: 'rgba(75, 192, 192, 0.6)' }, { label: '適當性', data: recentEvalFeedback.map(fb => getScore(fb.scores?.appropriateness)), backgroundColor: 'rgba(153, 102, 255, 0.6)' }, { label: '目標達成', data: recentEvalFeedback.map(fb => getScore(fb.scores?.goalAchievement)), backgroundColor: 'rgba(255, 99, 132, 0.6)' }, ].map(ds => ({ ...ds, borderColor: ds.backgroundColor.replace('0.6', '1'), borderWidth: 1 }));


  return (
    <Modal show={show} onHide={onHide} centered size="lg">
      <Modal.Header closeButton>
        <Modal.Title>與 "{character?.name}" 的對話評估</Modal.Title>
      </Modal.Header>
      <Modal.Body style={{maxHeight: '70vh', overflowY: 'auto'}}>

        {/* --- 添加目標搜尋框 --- */}
        <InputGroup size="sm" className="mb-3">
          <InputGroup.Text><i className="bi bi-search"></i></InputGroup.Text>
          <Form.Control
            type="text"
            placeholder="搜尋此角色的對話目標..."
            value={goalSearchModal}
            onChange={(e) => setGoalSearchModal(e.target.value)}
          />
        </InputGroup>
        {/* --- 結束目標搜尋框 --- */}

        {originalCharacterFeedback.length > 0 ? ( // 判斷是否有原始數據
          filteredCharacterFeedback.length > 0 ? ( // 判斷篩選後是否有數據
           <>
            {/* 圖表區域 */}
            {recentEvalFeedback.length > 0 && ( // 有數據才顯示圖表
              <div className="mb-4">
                <h5 className="text-center mb-3">最近 {recentEvalFeedback.length} 次表現趨勢</h5>
                <FeedbackChart labels={chartLabels} datasets={chartDataSets} />
              </div>
            )}

            {recentEvalFeedback.length > 0 && <hr />} {/* 有圖表才顯示分隔線 */}

            {/* 歷史記錄列表 */}
            <h5 className="mb-3">詳細回饋記錄 ({filteredCharacterFeedback.length} 筆)</h5>
            <ListGroup variant="flush">
              {/* 使用 filteredCharacterFeedback 渲染列表 */}
              {[...filteredCharacterFeedback].reverse().map(fb => ( // 從最新到最舊顯示
                <ListGroup.Item key={fb.timestamp} className="px-0">
                  {/* ... (列表項內容保持不變) ... */}
                   <div className="d-flex justify-content-between align-items-center mb-2"> <small className="text-muted"> {new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })} </small> <Badge bg="light" text="dark">目標: {fb.goal || '未指定'}</Badge> </div> <h6>AI 總結:</h6> <p style={{ whiteSpace: 'pre-wrap', fontSize: '0.95em' }}>{fb.summary || '-'}</p> {fb.scores && Object.values(fb.scores).some(s => s !== null) && ( <> <p className="small mb-1">評分:</p> <ul className="list-inline small mb-0"> <li className="list-inline-item me-2">清: <Badge pill bg={getScore(fb.scores.clarity) >= 60 ? "success" : "warning"}>{getScore(fb.scores.clarity)}</Badge></li> <li className="list-inline-item me-2">同: <Badge pill bg={getScore(fb.scores.empathy) >= 60 ? "success" : "warning"}>{getScore(fb.scores.empathy)}</Badge></li> <li className="list-inline-item me-2">自: <Badge pill bg={getScore(fb.scores.confidence) >= 60 ? "success" : "warning"}>{getScore(fb.scores.confidence)}</Badge></li> <li className="list-inline-item me-2">適: <Badge pill bg={getScore(fb.scores.appropriateness) >= 60 ? "success" : "warning"}>{getScore(fb.scores.appropriateness)}</Badge></li> <li className="list-inline-item">達: <Badge pill bg={getScore(fb.scores.goalAchievement) >= 60 ? "success" : "warning"}>{getScore(fb.scores.goalAchievement)}</Badge></li> </ul> </> )}
                </ListGroup.Item>
              ))}
            </ListGroup>
           </>
          ) : ( // 有原始數據，但篩選後沒有
             <Alert variant="warning">找不到符合目標關鍵字 "{goalSearchModal}" 的記錄。</Alert>
          )
        ) : ( // 完全沒有此角色的回饋記錄
          <Alert variant="info">還沒有與此角色的對話評估記錄。</Alert>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          關閉
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default CharacterEvalModal;
