// src/components/FeedbackWall/FeedbackWall.js (完整版 - 包含可收起篩選)

import React, { useState, useEffect, useMemo } from 'react';
import { Container, Row, Col, Card, Alert, Badge, InputGroup, Form, Button, Collapse } from 'react-bootstrap';
import ChartModal from './ChartModal'; // 導入圖表 Modal
import FeedbackChart from './FeedbackChart'; // ChartModal 會用到
import './FeedbackWall.css'; // 確保導入 CSS

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';

// 指標定義 (可以放在這裡或 utils 文件)
const ALL_METRICS = [
  { key: 'clarity', label: '清晰度', color: 'rgba(54, 162, 235, 0.6)' },
  { key: 'empathy', label: '同理心', color: 'rgba(255, 206, 86, 0.6)' },
  { key: 'confidence', label: '自信', color: 'rgba(75, 192, 192, 0.6)' },
  { key: 'appropriateness', label: '適當性', color: 'rgba(153, 102, 255, 0.6)' },
  { key: 'goalAchievement', label: '目標達成', color: 'rgba(255, 99, 132, 0.6)' },
];

function FeedbackWall() {
  const [originalFeedbackHistory, setOriginalFeedbackHistory] = useState([]);
  const [filteredFeedbackHistory, setFilteredFeedbackHistory] = useState([]);
  const [characterSearch, setCharacterSearch] = useState('');
  const [goalSearch, setGoalSearch] = useState('');
  const [showFilters, setShowFilters] = useState(true); // 篩選區域默認展開
  const [showChartModal, setShowChartModal] = useState(false); // 控制圖表 Modal

  // 加載數據
  useEffect(() => {
    const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
    let parsedFeedback = [];
    if (savedFeedback) {
      try {
        parsedFeedback = JSON.parse(savedFeedback);
        if (!Array.isArray(parsedFeedback)) parsedFeedback = [];
        parsedFeedback = parsedFeedback
          .filter(fb => typeof fb.timestamp === 'number')
          .sort((a, b) => b.timestamp - a.timestamp); // 初始按時間降序
      } catch (e) { console.error("Failed to parse feedback history", e); }
    }
    setOriginalFeedbackHistory(parsedFeedback);
    setFilteredFeedbackHistory(parsedFeedback); // 初始顯示所有數據
  }, []);

  // 執行篩選
  useEffect(() => {
    let result = [...originalFeedbackHistory];
    if (characterSearch.trim()) {
      const lowerCaseSearch = characterSearch.trim().toLowerCase();
      result = result.filter(fb => fb.characterName?.toLowerCase().includes(lowerCaseSearch));
    }
    if (goalSearch.trim()) {
      const lowerCaseGoal = goalSearch.trim().toLowerCase();
      result = result.filter(fb => fb.goal?.toLowerCase().includes(lowerCaseGoal));
    }
    setFilteredFeedbackHistory(result);
  }, [originalFeedbackHistory, characterSearch, goalSearch]);

  // 清除篩選
  const clearFilters = () => {
    setCharacterSearch('');
    setGoalSearch('');
  };

  // 提取分數輔助函數
  const getScore = (score) => (score === null || score === undefined ? 0 : score);

  return (
    <Container fluid className="feedback-wall-container p-0 d-flex flex-column">

      {/* --- 固定的頭部區域 --- */}
      <div className="feedback-wall-header d-flex justify-content-between align-items-center">
        <div>
          <h2 className="mb-1">回饋牆</h2>
          <p className="text-muted mb-0 small">這裡彙總了你每次請求 AI 回饋的結果。</p>
        </div>
        <Button variant="outline-primary" size="sm" onClick={() => setShowChartModal(true)} disabled={filteredFeedbackHistory.length === 0}>
           <i className="bi bi-graph-up me-1"></i> 查看趨勢圖表
        </Button>
      </div>

      {/* --- 可收起的篩選控制項區域 --- */}
      <div className="feedback-filters-container">
        <div className="d-flex justify-content-between align-items-center mb-2">
          <h6 className="mb-0 text-muted small">搜尋與篩選</h6>
          <Button
            variant="link"
            size="sm"
            onClick={() => setShowFilters(!showFilters)}
            aria-controls="collapse-feedback-filters"
            aria-expanded={showFilters}
            className="p-0 text-secondary"
          >
            {showFilters ? '收起' : '展開'} <i className={`bi bi-chevron-${showFilters ? 'up' : 'down'}`}></i>
          </Button>
        </div>
        <Collapse in={showFilters}>
          <div id="collapse-feedback-filters">
            <Row className="g-2 mb-2">
              <Col md={6}>
                <InputGroup size="sm">
                  <InputGroup.Text><i className="bi bi-person"></i></InputGroup.Text>
                  <Form.Control type="text" placeholder="搜尋角色名稱..." value={characterSearch} onChange={(e) => setCharacterSearch(e.target.value)}/>
                </InputGroup>
              </Col>
              <Col md={6}>
                <InputGroup size="sm">
                  <InputGroup.Text><i className="bi bi-chat-dots"></i></InputGroup.Text>
                  <Form.Control type="text" placeholder="搜尋對話目標關鍵字..." value={goalSearch} onChange={(e) => setGoalSearch(e.target.value)}/>
                </InputGroup>
              </Col>
            </Row>
            {(characterSearch || goalSearch) && (
              <Button variant="outline-secondary" size="sm" onClick={clearFilters} className="w-100">
                 <i className="bi bi-x-lg"></i> 清除所有篩選
              </Button>
            )}
          </div>
        </Collapse>
      </div>

      {/* --- 可滾動的歷史記錄列表 --- */}
      <div className="feedback-wall-body">
        <h4 className="h5 mb-3">詳細回饋記錄 ({filteredFeedbackHistory.length} 筆)</h4>
        {filteredFeedbackHistory.length > 0 ? (
          filteredFeedbackHistory.map((fb) => ( // *** 檢查這裡的語法 ***
            <Card key={fb.timestamp} className="shadow-sm"> {/* mb-3 移到 CSS 控制 */}
              <Card.Header className="d-flex justify-content-between align-items-center small text-muted flex-wrap">
                <span>{new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })}</span>
                <div className="mt-1 mt-md-0">
                  <Badge bg="secondary" className="me-2">角色: {fb.characterName || '未知'}</Badge>
                  <Badge bg="info" text="dark">目標: {fb.goal || '未知'}</Badge>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Subtitle className="mb-2 text-muted">AI 總結:</Card.Subtitle>
                <p style={{ whiteSpace: 'pre-wrap' }}>{fb.summary || '-'}</p>
                {fb.scores && Object.values(fb.scores).some(s => s !== null) && (
                  <>
                    <hr />
                    <p className="small mb-1">評分細節:</p>
                    <ul className="list-inline small mb-0">
                      {/* 使用 ALL_METRICS 來動態生成列表項，更易維護 */}
                      {ALL_METRICS.map(metric => (
                        <li key={metric.key} className="list-inline-item me-3">
                          {metric.label}: <Badge pill bg={getScore(fb.scores[metric.key]) >= 60 ? "success" : "warning"}>
                            {getScore(fb.scores[metric.key]) ?? 'N/A'} {/* 處理 null */}
                          </Badge>
                        </li>
                      ))}
                    </ul>
                  </>
                )}
              </Card.Body>
            </Card>
          )) // *** 確保括號閉合正確 ***
        ) : (
          <Alert variant="secondary" className="text-center">
             {characterSearch || goalSearch ? '找不到符合篩選條件的回饋記錄。' : '還沒有任何回饋記錄。'}
          </Alert>
        )}
      </div>

      {/* --- 圖表 Modal --- */}
      <ChartModal
         show={showChartModal}
         onHide={() => setShowChartModal(false)}
         feedbackData={filteredFeedbackHistory} // 傳遞篩選後的數據
       />

    </Container>
  );
}

export default FeedbackWall;
