// src/components/CharacterHall/CharacterHall.js (添加查看評估和編輯功能)

import React, { useState } from 'react';
import { Button, Row, Col, Card, Alert as BootstrapAlert, Container, ButtonGroup } from 'react-bootstrap'; // 導入 Container 和 ButtonGroup
import AddCharacterModal from './AddCharacterModal';
import CharacterEvalModal from './CharacterEvalModal'; // <<<--- 導入新增的評估 Modal
import './CharacterHall.css'; // 假設有這個 CSS 文件

function CharacterHall({ characters, onUpdateCharacters, onSelectCharacter }) {
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEvalModal, setShowEvalModal] = useState(false); // <<<--- 新增 Eval Modal 狀態
  const [editingCharacter, setEditingCharacter] = useState(null); // <<<--- 新增正在編輯的角色狀態
  const [evaluatingCharacter, setEvaluatingCharacter] = useState(null); // <<<--- 新增正在評估的角色狀態

  // --- 新增/編輯相關 ---
  const handleShowAddModal = () => {
    setEditingCharacter(null); // 確保是新增模式
    setShowAddModal(true);
  }

  const handleShowEditModal = (character) => {
    setEditingCharacter(character); // 設置要編輯的角色
    setShowAddModal(true); // 打開同一個 Modal
  }

  const handleHideAddModal = () => {
    setShowAddModal(false);
    setEditingCharacter(null); // 清除編輯狀態
  }

  // 處理新增或更新請求 (合併邏輯)
  const handleSaveCharacterRequest = (characterData) => {
    const trimmedName = characterData.name.trim();
    const isDuplicate = characters.some(
      (c) => c.id !== editingCharacter?.id && // 檢查名稱是否與 *其他* 角色重複
             c.name.trim().toLowerCase() === trimmedName.toLowerCase()
    );

    if (isDuplicate) {
      console.warn(`重複的角色名稱: "${trimmedName}"`);
      return { success: false, error: `角色名稱 "${trimmedName}" 已經存在，請修改。` };
    }

    if (editingCharacter) {
      // --- 更新邏輯 ---
      const updatedCharacters = characters.map(c =>
        c.id === editingCharacter.id ? { ...c, name: trimmedName, description: characterData.description } : c
      );
      onUpdateCharacters(updatedCharacters);
      console.log("角色更新成功");
      return { success: true };
    } else {
      // --- 新增邏輯 ---
      const characterWithId = { ...characterData, name: trimmedName, id: `char_${Date.now()}_${Math.random().toString(36).substring(2, 7)}` };
      const updatedCharacters = [...characters, characterWithId];
      onUpdateCharacters(updatedCharacters);
      console.log("角色新增成功");
      return { success: true };
    }
  };

  // --- 評估相關 ---
  const handleShowEvalModal = (character) => {
    setEvaluatingCharacter(character);
    setShowEvalModal(true);
  }

  const handleHideEvalModal = () => {
    setShowEvalModal(false);
    setEvaluatingCharacter(null);
  }

  // --- 刪除相關 (保持不變) ---
  const handleDeleteCharacter = (idToDelete) => { /* ... */ if (idToDelete === 'default') { alert("無法刪除預設助手角色。"); return; } const characterToDelete = characters.find(c => c.id === idToDelete); if (characterToDelete && window.confirm(`確定要刪除角色 "${characterToDelete.name}" 嗎？`)) { const updatedCharacters = characters.filter(c => c.id !== idToDelete); onUpdateCharacters(updatedCharacters); } };

  return (
    <Container fluid className="character-hall-container p-0 d-flex flex-column">
      {/* --- 固定的頭部區域 --- */}
      <div className="character-hall-header bg-light p-3 shadow-sm">
        <h2 className="mb-2">角色館</h2>
        <p className="text-muted mb-3">在這裡新增、管理你想互動練習的 AI 角色。選擇一個角色後，點擊「開始訓練」即可進入訓練室。</p>
        <Button variant="primary" onClick={handleShowAddModal}> {/* 改為調用 handleShowAddModal */}
          <i className="bi bi-plus-lg me-2"></i>新增角色
        </Button>
      </div>

      {/* --- 可滾動的角色卡片區域 --- */}
      <div className="character-hall-body p-4">
        {characters.filter(c => c.id !== 'default').length === 0 && (
          <BootstrapAlert variant="info">目前沒有任何自訂角色，快新增一個吧！</BootstrapAlert>
        )}

        <Row xs={1} md={2} lg={3} xl={4} className="g-4">
          {characters.map((character) => (
            <Col key={character.id}>
              <Card className="h-100 shadow-sm character-card">
                <Card.Body className="d-flex flex-column">
                  <Card.Title>{character.name}</Card.Title>
                  <Card.Text className="text-muted small flex-grow-1 character-description">
                    {character.description}
                  </Card.Text>
                  {/* --- 按鈕組 --- */}
                  <div className="mt-auto d-flex justify-content-between align-items-center pt-2">
                    <Button
                      variant="success"
                      size="sm"
                      onClick={() => onSelectCharacter(character)}
                      title="選擇此角色並開始訓練"
                      className="me-1" // 添加間距
                    >
                      <i className="bi bi-chat-dots-fill me-1"></i> 訓練
                    </Button>

                    {/* --- 評估、編輯、刪除按鈕組 (非預設角色) --- */}
                    {character.id !== 'default' && (
                      <ButtonGroup size="sm">
                        <Button
                          variant="outline-info"
                          onClick={() => handleShowEvalModal(character)} // <<<--- 查看評估按鈕
                          title="查看對話評估"
                        >
                          <i className="bi bi-graph-up"></i> {/* 圖表圖標 */}
                        </Button>
                        <Button
                          variant="outline-secondary"
                          onClick={() => handleShowEditModal(character)} // <<<--- 編輯按鈕
                          title="編輯此角色"
                        >
                          <i className="bi bi-pencil-square"></i> {/* 編輯圖標 */}
                        </Button>
                        <Button
                          variant="outline-danger"
                          onClick={() => handleDeleteCharacter(character.id)}
                          title="刪除此角色"
                        >
                          <i className="bi bi-trash3"></i>
                        </Button>
                      </ButtonGroup>
                    )}
                     {/* 預設角色占位符 */}
                    {character.id === 'default' && (
                       <span style={{ minWidth: '90px' }}></span> // 調整占位寬度以對齊
                     )}
                  </div>
                  {/* --- 結束按鈕組 --- */}
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </div>

      {/* --- 新增/編輯 Modal --- */}
      <AddCharacterModal
        show={showAddModal}
        onHide={handleHideAddModal}
        onSaveCharacterRequest={handleSaveCharacterRequest} // <<<--- 修改 prop 名稱
        initialData={editingCharacter} // <<<--- 傳遞初始數據給 Modal
      />

      {/* --- 評估 Modal --- */}
      {evaluatingCharacter && ( // 僅在有評估對象時渲染
          <CharacterEvalModal
            show={showEvalModal}
            onHide={handleHideEvalModal}
            character={evaluatingCharacter} // <<<--- 傳遞要評估的角色
          />
      )}

    </Container>
  );
}

export default CharacterHall;
