// src/components/FeedbackWall/FeedbackChart.js (修正版)

import React from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function FeedbackChart({ labels, datasets }) {
  const data = {
    labels: labels,
    datasets: datasets,
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: '社交技能評分趨勢 (0-100分)',
      },
      tooltip: {
         mode: 'index',
         intersect: false,
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        max: 100,
        title: {
           display: true,
           text: '分數'
        }
      },
      x: {
         title: {
            display: true,
            text: '練習時間'
         },
         ticks: {
             autoSkip: true, // 自動跳過一些標籤以避免重疊
             maxRotation: 45, // 允許標籤最大旋轉 45 度
             minRotation: 0   // 最小旋轉 0 度
         }
      }
    },
    interaction: {
      mode: 'index',
      intersect: false,
    },
  };

  return (
    <div style={{ position: 'relative', height: '300px', width: '100%' }}>
       <Bar options={options} data={data} />
    </div>
   );
}

export default FeedbackChart;
