// src/components/TrainingRoom/TrainingRoom.js (‰øÆÊîπ handleClearChat ‰ª•ÂêåÊ≠•Âà™Èô§ÂõûÈ•ã)

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Form, InputGroup, Container, Row, Col, Spinner, Alert, Button, Dropdown } from 'react-bootstrap';
import ChatDisplay from './ChatDisplay';
import MessageInput from './MessageInput';
import { sendMessageToOllama, getFeedbackFromOllama } from '../../services/ollamaService'; // Á¢∫‰øùË∑ØÂæëÊ≠£Á¢∫
import '../../styles/TrainingRoom.css'; // Á¢∫‰øùË∑ØÂæëÊ≠£Á¢∫

const CHAT_HISTORY_PREFIX = 'wingchat_history_';
const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';

function TrainingRoom({ characters, selectedCharacter, onSelectCharacter, setActiveView }) { // Êé•Êî∂ÊâÄÊúâÈúÄË¶ÅÁöÑ props
  const [goal, setGoal] = useState('');
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isGettingFeedback, setIsGettingFeedback] = useState(false);
  const [error, setError] = useState(null);
  const currentGoalRef = useRef(goal);

  // Áç≤Âèñ localStorage key ÁöÑÂáΩÊï∏
  const getStorageKey = useCallback(() => {
      if (!selectedCharacter || !goal.trim()) return null;
      const goalKey = goal.trim().substring(0, 50).replace(/[^a-zA-Z0-9-_]/g, '_');
      return `${CHAT_HISTORY_PREFIX}${selectedCharacter.id}_${goalKey}`;
  }, [selectedCharacter, goal]);

  // Âä†ËºâËÅäÂ§©Ë®òÈåÑ
  useEffect(() => {
    currentGoalRef.current = goal;
    const storageKey = getStorageKey();
    if (storageKey) {
      const savedHistory = localStorage.getItem(storageKey);
      if (savedHistory) { try { setMessages(JSON.parse(savedHistory)); } catch (e) { console.error("Parsing chat history failed", e); setMessages([]); } }
      else { setMessages([]); }
    } else if (selectedCharacter) { setMessages([]); } // ÊúâËßíËâ≤‰ΩÜÊ≤íÁõÆÊ®ôÊàñkeyÔºå‰πüÊ∏ÖÁ©∫
     setError(null); // ÁõÆÊ®ôÊàñËßíËâ≤ÊîπËÆäÊôÇÊ∏ÖÈô§ÈåØË™§
  }, [selectedCharacter, goal, getStorageKey]); // Áï∂ËßíËâ≤ÊàñÁõÆÊ®ôÊîπËÆäÊôÇËß∏ÁôºÂä†Ëºâ

   // ‰øùÂ≠òËÅäÂ§©Ë®òÈåÑ
   useEffect(() => {
     const storageKey = getStorageKey();
     if (storageKey && messages.length > 0) { localStorage.setItem(storageKey, JSON.stringify(messages)); }
     else if (storageKey && messages.length === 0 && localStorage.getItem(storageKey)) { // Á¢∫‰øùÂÉÖÂú®‰πãÂâçÊúâË®òÈåÑÊôÇÊâçÁßªÈô§
         localStorage.removeItem(storageKey);
         console.log(`Removed chat history from localStorage on clear: ${storageKey}`);
     }
   }, [messages, getStorageKey]); // Áï∂ messages ËÆäÂåñÊôÇËß∏Áôº‰øùÂ≠òÊàñÁßªÈô§

  // --- ‰øÆÊîπÔºöÊ∏ÖÈô§ËÅäÂ§©Ë®òÈåÑÊôÇÔºåÂêåÊ≠•Âà™Èô§Áõ∏ÈóúÂõûÈ•ã ---
  const handleClearChat = () => {
      if (!selectedCharacter || !goal.trim()) return; // Á¢∫‰øùÊúâËßíËâ≤ÂíåÁõÆÊ®ô

      const characterName = selectedCharacter.name;
      const currentGoal = goal.trim();
      const characterId = selectedCharacter.id;

      if (window.confirm(`Á¢∫ÂÆöË¶ÅÊ∏ÖÈô§Ëàá ${characterName} ÈóúÊñºÁõÆÊ®ô„Äå${currentGoal}„ÄçÁöÑÊâÄÊúâËÅäÂ§©Ë®òÈåÑÂóéÔºü\n\n‚ö†Ô∏è ÈÄôÂ∞áÂêåÊôÇÂà™Èô§ÊâÄÊúâËàáÊ≠§Â∞çË©±Áõ∏ÈóúÁöÑÂõûÈ•ãË®òÈåÑÔºÅ`)) {
          // 1. Ê∏ÖÈô§Áï∂ÂâçËÅäÂ§©Ë®òÈåÑÁãÄÊÖã
          setMessages([]); // ÈÄôÊúÉËß∏Áôº‰∏äÈù¢ÁöÑ useEffect ‰æÜÁßªÈô§ localStorage ‰∏≠ÁöÑËÅäÂ§©Ë®òÈåÑ

          // 2. Âæû localStorage ÁßªÈô§Áõ∏ÈóúÂõûÈ•ãË®òÈåÑ
          try {
              const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
              let allFeedback = [];
              if (savedFeedback) {
                  allFeedback = JSON.parse(savedFeedback);
                  if (!Array.isArray(allFeedback)) allFeedback = [];
              }

              // ÈÅéÊøæÊéâ characterId Âíå goal ÈÉΩÂåπÈÖçÁöÑË®òÈåÑ
              const updatedFeedbackHistory = allFeedback.filter(fb =>
                  !(fb.characterId === characterId && fb.goal === currentGoal)
              );

              // Â∞áÈÅéÊøæÂæåÁöÑÂõûÈ•ãÂàóË°®Â≠òÂõû localStorage
              localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify(updatedFeedbackHistory));
              console.log(`Removed ${allFeedback.length - updatedFeedbackHistory.length} feedback records for characterId=${characterId} and goal=${currentGoal}`);

          } catch (e) {
              console.error("Ê∏ÖÈô§Áõ∏ÈóúÂõûÈ•ãË®òÈåÑÊôÇÂá∫ÈåØ:", e);
              alert("Ê∏ÖÈô§Áõ∏ÈóúÂõûÈ•ãË®òÈåÑÊôÇÁôºÁîüÈåØË™§„ÄÇ"); // ÂèØ‰ª•ÊèêÁ§∫Áî®Êà∂
          }
          // --- ÁµêÊùüÂêåÊ≠•Âà™Èô§ ---
      }
  }

  // ÁôºÈÄÅË®äÊÅØËôïÁêÜ
  const handleSendMessage = async () => {
    if (!currentMessage.trim() || !selectedCharacter) return;
    if (!goal.trim()) { setError("Ë´ãÂÖàË®≠ÂÆöÊú¨Ê¨°ËÅäÂ§©ÁöÑÁõÆÊ®ôÔºÅ"); return; }
    if (isLoading || isGettingFeedback) return;
    const newUserMessage = { id: Date.now(), sender: 'user', text: currentMessage };
    const currentInput = currentMessage;
    setMessages(prevMessages => [...prevMessages, newUserMessage]);
    setCurrentMessage(''); setIsLoading(true); setError(null);
    try {
      const historyForOllama = messages.map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text }));
      const requestHistory = [...historyForOllama, { role: 'user', content: currentInput }];
      const chatModel = process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'huayi-llama3:latest'; // ‰ΩøÁî®‰Ω†ÁöÑÊ®°ÂûãÂêç
      const aiResponseText = await sendMessageToOllama(goal, requestHistory, selectedCharacter, chatModel);
      const newAiMessage = { id: Date.now() + 1, sender: 'ai', text: aiResponseText };
      setMessages(prevMessages => [...prevMessages, newAiMessage]);
    } catch (err) {
      console.error("Error contacting Ollama:", err);
      const errorMsg = err.message || "Ëàá AI ÈÄöË®äÊôÇÁôºÁîüÈåØË™§„ÄÇ";
      setError(errorMsg);
      const errorAiMessage = { id: Date.now() + 1, sender: 'ai', text: `ü§ñ Êä±Ê≠âÔºåËôïÁêÜÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}` };
      setMessages(prevMessages => [...prevMessages, errorAiMessage]);
    } finally { setIsLoading(false); }
  };

  // ÂõûÈ•ãËß£ÊûêÂáΩÊï∏
  const parseFeedback = (feedbackText) => {
      let summary = "ÁÑ°Ê≥ïËß£ÊûêÂõûÈ•ãÊëòË¶Å„ÄÇ"; let scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null };
      let summaryMatch = null; let summaryOnlyMatch = null; let scoresTextMatch = null;
      try {
          summaryMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*?)\s*\[Scores\]/);
          if (summaryMatch && summaryMatch[1]) { summary = summaryMatch[1].trim(); }
          else { summaryOnlyMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*)/); if (summaryOnlyMatch && summaryOnlyMatch[1]) { summary = summaryOnlyMatch[1].trim(); console.warn("Only found summary..."); } else { console.warn("Could not find [Feedback Summary] section."); summary = feedbackText; } }
          scoresTextMatch = feedbackText.match(/\[Scores\]\s*([\s\S]*)/);
          if (scoresTextMatch && scoresTextMatch[1]) { const scoreLines = scoresTextMatch[1].trim().split('\n'); scoreLines.forEach(line => { const parts = line.split(':'); if (parts.length === 2) { let key = parts[0].trim().toLowerCase().replace(/\s+/g, ''); if (key === 'goalachievement') key = 'goalAchievement'; const value = parts[1].trim(); if (Object.prototype.hasOwnProperty.call(scores, key)) { if (value.toLowerCase() === 'n/a') { scores[key] = null; } else { const score = parseInt(value, 10); if (!isNaN(score)) { scores[key] = Math.max(0, Math.min(100, score)); } else { console.warn(`Invalid score value "${value}" for key "${key}"`); scores[key] = null; } } } else { console.warn(`Unexpected score key "${key}" found.`); } } });
          } else if (!summaryOnlyMatch && summaryMatch === null){ console.warn("Could not find [Scores] section..."); }
      } catch (e) { console.error("Error parsing feedback text:", e); summary = feedbackText; scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null }; }
      console.log("Parsed Feedback:", { summary, scores }); return { summary, scores };
  };

  // Áç≤ÂèñÂõûÈ•ãËôïÁêÜ
  const handleGetFeedback = async () => {
      if (!selectedCharacter || !goal.trim() || messages.filter(m => m.sender !== 'system').length === 0) { alert("Ë´ãÂÖàË®≠ÂÆöÁõÆÊ®ô„ÄÅÈÅ∏ÊìáËßíËâ≤‰∏¶ÈÄ≤Ë°åËá≥Â∞ë‰∏ÄËº™Â∞çË©±ÔºåÊâçËÉΩË´ãÊ±ÇÂõûÈ•ã„ÄÇ"); return; }
      if (isLoading || isGettingFeedback) return;
      setIsGettingFeedback(true); setError(null); const feedbackRequestMessage = { id: Date.now(), sender: 'system', text: '‚è≥ Ê≠£Âú®Âêë AI Ë´ãÊ±ÇÂ∞çË©±ÂõûÈ•ã...' }; setMessages(prevMessages => [...prevMessages, feedbackRequestMessage]);
      try {
          const historyForFeedback = messages.filter(msg => msg.sender !== 'system').map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text }));
          const feedbackModel = process.env.REACT_APP_OLLAMA_FEEDBACK_MODEL || process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'huayi-llama3:latest'; // ‰ΩøÁî®‰Ω†ÁöÑÊ®°ÂûãÂêç
          const feedbackRawText = await getFeedbackFromOllama(goal, historyForFeedback, selectedCharacter, feedbackModel);
          const { summary, scores } = parseFeedback(feedbackRawText);
          const feedbackMessage = { id: Date.now() + 1, sender: 'ai', isFeedback: true, text: `üí° **Êú¨Ê¨°Â∞çË©±ÂõûÈ•ã (${goal})**\n\n**ÊëòË¶Å:**\n${summary}\n\n**Ë©ïÂàÜ (0-100):**\n- Ê∏ÖÊô∞Â∫¶: ${scores.clarity ?? 'N/A'}\n- ÂêåÁêÜÂøÉ: ${scores.empathy ?? 'N/A'}\n- Ëá™‰ø°: ${scores.confidence ?? 'N/A'}\n- ÈÅ©Áï∂ÊÄß: ${scores.appropriateness ?? 'N/A'}\n- ÁõÆÊ®ôÈÅîÊàê: ${scores.goalAchievement ?? 'N/A'}` };
          const newFeedbackEntry = { timestamp: Date.now(), goal: goal, characterId: selectedCharacter.id, characterName: selectedCharacter.name, scores: scores, summary: summary, rawFeedback: feedbackRawText };
          const existingFeedback = JSON.parse(localStorage.getItem(FEEDBACK_STORAGE_KEY) || '[]'); localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify([...existingFeedback, newFeedbackEntry]));
          setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), feedbackMessage]);
      } catch (err) {
          console.error("Error getting or parsing feedback from Ollama:", err); const errorMsg = err.message || "ÁÑ°Ê≥ïÁç≤ÂèñÊàñËôïÁêÜ AI ÂõûÈ•ã„ÄÇ"; setError(errorMsg); const errorFeedbackMessage = { id: Date.now() + 1, sender: 'ai', text: `ü§ñ Êä±Ê≠âÔºåÁç≤ÂèñÂõûÈ•ãÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}` }; setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), errorFeedbackMessage]);
      } finally { setIsGettingFeedback(false); }
  };

  return (
    // Á¢∫‰øù‰ΩøÁî®‰∫Ü training-room-container class ‰∏¶‰∏î p-0
    <Container fluid className="training-room-container p-0">
      {/* È†ÇÈÉ®Ê¨Ñ */}
      <Row className="goal-input-row m-0 align-items-center">
        <Col md={7} className="mb-2 mb-md-0">
          <InputGroup>
            <InputGroup.Text>üéØ</InputGroup.Text>
            <Form.Control type="text" placeholder="Ëº∏ÂÖ•Êú¨Ê¨°Á§æ‰∫§Ë®ìÁ∑¥ÁõÆÊ®ô..." value={goal} onChange={(e) => setGoal(e.target.value)} disabled={isLoading || isGettingFeedback} aria-label="ËÅäÂ§©ÁõÆÊ®ô"/>
          </InputGroup>
        </Col>
        <Col md={5} className="d-flex align-items-center justify-content-md-end">
          {characters && characters.length > 0 ? (
            <Dropdown className="me-3" id="character-selector-dropdown">
              <Dropdown.Toggle variant="outline-secondary" size="sm" id="dropdown-character-select-button" disabled={isLoading || isGettingFeedback} style={{minWidth: '120px', textAlign: 'left'}}>
                Ëàá: {selectedCharacter ? <strong className="text-primary">{selectedCharacter.name}</strong> : 'ÈÅ∏ÊìáËßíËâ≤'}
              </Dropdown.Toggle>
              <Dropdown.Menu style={{maxHeight: '300px', overflowY: 'auto'}}>
                {characters.map(character => (
                  <Dropdown.Item key={character.id} active={selectedCharacter?.id === character.id} onClick={() => onSelectCharacter(character)} disabled={selectedCharacter?.id === character.id}>
                    {character.name}
                  </Dropdown.Item>
                ))}
                 <Dropdown.Divider />
                 {/* Á¢∫‰øù setActiveView prop Ë¢´ÂÇ≥ÈÅû */}
                 <Dropdown.Item onClick={() => setActiveView && setActiveView('characters')}>
                   <small className="text-muted"> <i className="bi bi-gear me-1"></i> ÁÆ°ÁêÜËßíËâ≤... </small>
                 </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
           ) : ( <span className="text-danger me-3 small">Ë´ãÂÖàËá≥ËßíËâ≤È§®Êñ∞Â¢ûËßíËâ≤</span> )}
          {selectedCharacter && goal.trim() && messages.length > 0 && (
            <Button variant="outline-danger" size="sm" onClick={handleClearChat} disabled={isLoading || isGettingFeedback} title={`Ê∏ÖÈô§Ëàá ${selectedCharacter.name} ÈóúÊñºÁõÆÊ®ô "${goal}" ÁöÑËÅäÂ§©Ë®òÈåÑ`}>
              <i className="bi bi-trash3"></i>
            </Button>
          )}
        </Col>
        {error && ( <Col xs={12} className="mt-2"><Alert variant="danger" onClose={() => setError(null)} dismissible className="py-1 px-2 small mb-0">{error}</Alert></Col> )}
        {(!selectedCharacter || !goal.trim()) && messages.length === 0 && !error && ( <Col xs={12} className="mt-2"><Alert variant="info" className="text-center small p-2 mb-0"> Ë´ãÂÖàÈÅ∏ÊìáËßíËâ≤‰∏¶Ë®≠ÂÆöÁõÆÊ®ôÔºåÁÑ∂ÂæåÈñãÂßãÂ∞çË©±ÔºÅ </Alert></Col> )}
      </Row>

      {/* ËÅäÂ§©È°ØÁ§∫ÂçÄÂüü */}
      <Row className="chat-display-row m-0">
        <Col className="p-0">
          <ChatDisplay messages={messages} isLoading={isLoading} />
        </Col>
      </Row>

      {/* Ë®äÊÅØËº∏ÂÖ•ÂçÄÂüü */}
      <Row className="message-input-row m-0">
        <Col>
          <MessageInput
            currentMessage={currentMessage}
            onMessageChange={setCurrentMessage}
            onSendMessage={handleSendMessage}
            onGetFeedback={handleGetFeedback}
            isLoading={isLoading}
            isGettingFeedback={isGettingFeedback}
            disabled={!selectedCharacter || !goal.trim() || isLoading || isGettingFeedback}
          />
        </Col>
      </Row>
    </Container>
  );
}

export default TrainingRoom;
