// src/components/FeedbackWall/FeedbackWall.js (修正版)

import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Alert, Badge } from 'react-bootstrap'; // 引入 Badge
import FeedbackChart from './FeedbackChart';

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';

function FeedbackWall() {
  const [feedbackHistory, setFeedbackHistory] = useState([]);

  useEffect(() => {
    const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
    let parsedFeedback = [];
    if (savedFeedback) {
      try {
        parsedFeedback = JSON.parse(savedFeedback);
        if (!Array.isArray(parsedFeedback)) { // 確保是陣列
          parsedFeedback = [];
        }
        // 按時間戳確保是數字且降序排序
        parsedFeedback = parsedFeedback
          .filter(fb => typeof fb.timestamp === 'number') // 過濾掉無效時間戳
          .sort((a, b) => b.timestamp - a.timestamp);
      } catch (e) {
        console.error("Failed to parse feedback history", e);
        parsedFeedback = [];
      }
    }
    setFeedbackHistory(parsedFeedback);
  }, []); // 僅在組件加載時運行一次

  // --- 準備圖表數據 ---
  const recentFeedback = feedbackHistory.slice(0, 5).reverse(); // 取最近最多 5 筆並反轉 (舊到新)

  // 改進日期格式，如果日期相同，加上時間區分
  const chartLabels = recentFeedback.map((fb, index, arr) => {
    const date = new Date(fb.timestamp);
    const dateString = date.toLocaleDateString('zh-TW', { month: 'numeric', day: 'numeric' }); // 例如 '4/26'
    const timeString = date.toLocaleTimeString('zh-TW', { hour: '2-digit', minute: '2-digit', hour12: false }); // 例如 '15:18'

    // 檢查是否有重複日期標籤
    const prevDateString = index > 0 ? new Date(arr[index - 1].timestamp).toLocaleDateString('zh-TW', { month: 'numeric', day: 'numeric' }) : null;
    if (dateString === prevDateString) {
      return `${dateString} ${timeString}`; // 日期相同則加上時間
    }
    return dateString; // 否則只顯示日期
  });

  // 確保即使某項分數是 null 也能處理，給予預設值 0
  const getScore = (score) => (score === null || score === undefined ? 0 : score);

  const chartDataSets = [
    { label: '清晰度', data: recentFeedback.map(fb => getScore(fb.scores?.clarity)), backgroundColor: 'rgba(54, 162, 235, 0.6)', borderColor: 'rgba(54, 162, 235, 1)', borderWidth: 1 },
    { label: '同理心', data: recentFeedback.map(fb => getScore(fb.scores?.empathy)), backgroundColor: 'rgba(255, 206, 86, 0.6)', borderColor: 'rgba(255, 206, 86, 1)', borderWidth: 1 },
    { label: '自信', data: recentFeedback.map(fb => getScore(fb.scores?.confidence)), backgroundColor: 'rgba(75, 192, 192, 0.6)', borderColor: 'rgba(75, 192, 192, 1)', borderWidth: 1 },
    { label: '適當性', data: recentFeedback.map(fb => getScore(fb.scores?.appropriateness)), backgroundColor: 'rgba(153, 102, 255, 0.6)', borderColor: 'rgba(153, 102, 255, 1)', borderWidth: 1 },
    { label: '目標達成', data: recentFeedback.map(fb => getScore(fb.scores?.goalAchievement)), backgroundColor: 'rgba(255, 99, 132, 0.6)', borderColor: 'rgba(255, 99, 132, 1)', borderWidth: 1 },
  ];

  return (
    <Container>
      <h2 className="mb-4">回饋牆</h2>
      <p className="text-muted mb-4">這裡彙總了你每次請求 AI 回饋的結果，觀察你的進步趨勢吧！</p>

      {/* --- 進步趨勢圖表 --- */}
      <Card className="mb-4 shadow-sm">
        <Card.Header>最近 {recentFeedback.length} 次練習表現趨勢</Card.Header>
        <Card.Body>
          {recentFeedback.length > 0 ? (
            <FeedbackChart labels={chartLabels} datasets={chartDataSets} />
          ) : (
            <Alert variant="light" className="text-center">
              還沒有回饋數據來顯示趨勢圖。在訓練室完成對話後，試試點擊「我說得如何？」按鈕吧！
            </Alert>
          )}
        </Card.Body>
      </Card>

      {/* --- 歷史回饋列表 --- */}
      <h3 className="h5 mb-3">歷史回饋記錄</h3>
      {feedbackHistory.length > 0 ? (
        feedbackHistory.map((fb) => (
          <Card key={fb.timestamp} className="mb-3 shadow-sm">
            <Card.Header className="d-flex justify-content-between align-items-center small text-muted flex-wrap"> {/* 添加 flex-wrap */}
              <span>{new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })}</span>
              {/* 使用 Badge 強調角色和目標 */}
              <div className="mt-1 mt-md-0"> {/* 避免小螢幕時換行 */}
                <Badge bg="secondary" className="me-2">角色: {fb.characterName || '未知'}</Badge>
                <Badge bg="info" text="dark">目標: {fb.goal || '未知'}</Badge>
              </div>
            </Card.Header>
            <Card.Body>
              <Card.Subtitle className="mb-2 text-muted">AI 總結:</Card.Subtitle>
              {/* 使用 pre-wrap 保留換行符 */}
              <p style={{ whiteSpace: 'pre-wrap' }}>{fb.summary || '沒有提供總結。'}</p>
              {/* 顯示分數 (如果存在) */}
              {fb.scores && Object.values(fb.scores).some(s => s !== null) && (
                <>
                  <hr />
                  <p className="small mb-1">評分細節:</p>
                  <ul className="list-inline small">
                    <li className="list-inline-item me-3">清晰度: <Badge pill bg={getScore(fb.scores.clarity) >= 60 ? "success" : "warning"}>{getScore(fb.scores.clarity)}</Badge></li>
                    <li className="list-inline-item me-3">同理心: <Badge pill bg={getScore(fb.scores.empathy) >= 60 ? "success" : "warning"}>{getScore(fb.scores.empathy)}</Badge></li>
                    <li className="list-inline-item me-3">自信: <Badge pill bg={getScore(fb.scores.confidence) >= 60 ? "success" : "warning"}>{getScore(fb.scores.confidence)}</Badge></li>
                    <li className="list-inline-item me-3">適當性: <Badge pill bg={getScore(fb.scores.appropriateness) >= 60 ? "success" : "warning"}>{getScore(fb.scores.appropriateness)}</Badge></li>
                    <li className="list-inline-item">目標達成: <Badge pill bg={getScore(fb.scores.goalAchievement) >= 60 ? "success" : "warning"}>{getScore(fb.scores.goalAchievement)}</Badge></li>
                  </ul>
                </>
              )}
            </Card.Body>
          </Card>
        ))
      ) : (
        <Alert variant="secondary" className="text-center">
          還沒有任何回饋記錄。
        </Alert>
      )}
    </Container>
  );
}

export default FeedbackWall;
