// src/components/CharacterHall/CharacterEvalModal.js (再次檢查和優化)

import React, { useState, useEffect, useMemo } from 'react';
import { Modal, Button, Alert, ListGroup, Badge, Collapse, Form, InputGroup, Row, Col } from 'react-bootstrap';
import ChartModal from '../FeedbackWall/ChartModal'; // 確保 ChartModal 能正確導入和使用
import FeedbackChart from '../FeedbackWall/FeedbackChart'; // 如果 ChartModal 複用
import './CharacterHall.css'; // 確保導入 CSS

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';
// 再次確認指標定義與 FeedbackWall 一致
const ALL_METRICS = [ { key: 'clarity', label: '清晰度' }, { key: 'empathy', label: '同理心' }, { key: 'confidence', label: '自信' }, { key: 'appropriateness', label: '適當性' }, { key: 'goalAchievement', label: '目標達成' }, ];
const getScore = (score) => (score === null || score === undefined ? 0 : score);

function CharacterEvalModal({ show, onHide, character, onDeleteFeedback }) { // 確保 onDeleteFeedback prop 傳遞正確
  // --- 狀態管理 ---
  const [originalCharacterFeedback, setOriginalCharacterFeedback] = useState([]);
  const [filteredCharacterFeedback, setFilteredCharacterFeedback] = useState([]);
  const [goalSearchModal, setGoalSearchModal] = useState('');
  const [showEvalFilters, setShowEvalFilters] = useState(false); // 篩選器默認收起
  const [showEvalChartModal, setShowEvalChartModal] = useState(false); // 控制圖表 Modal

  // --- 加載和過濾數據 ---
  useEffect(() => {
    let loadedFeedback = []; // 先定義在 try 外部
    if (show && character) {
      console.log(`EvalModal - Loading feedback for character: ${character.name} (ID: ${character.id})`); // 調試日誌
      const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
      let allFeedback = [];
      if (savedFeedback) {
        try {
          allFeedback = JSON.parse(savedFeedback);
          if (!Array.isArray(allFeedback)) allFeedback = [];
        } catch (e) {
          console.error("EvalModal - Failed to parse feedback history", e);
          allFeedback = [];
        }
      } else {
          console.log("EvalModal - No feedback history found in localStorage.");
      }

      // 篩選出屬於當前角色的回饋
      loadedFeedback = allFeedback
        .filter(fb => {
            const idMatch = fb.characterId === character.id;
            const validTimestamp = typeof fb.timestamp === 'number';
            // console.log(`Filtering: fb.id=${fb.characterId}, targetId=${character.id}, match=${idMatch}, ts=${fb.timestamp}, validTs=${validTimestamp}`); // 詳細調試
            return idMatch && validTimestamp;
        })
        .sort((a, b) => b.timestamp - a.timestamp); // 按時間降序存儲原始數據

      console.log(`EvalModal - Found ${loadedFeedback.length} records for this character.`);
      setOriginalCharacterFeedback(loadedFeedback); // 更新原始數據
      // 在這裡也應用一次初始篩選 (雖然目標搜尋為空)
      setFilteredCharacterFeedback(loadedFeedback);

      // 重置篩選和展開狀態
      setGoalSearchModal('');
      setShowEvalFilters(false); // 每次打開默認收起
      setShowEvalChartModal(false); // 確保圖表 Modal 關閉

    } else {
      // Modal 關閉或 character 為空時清空狀態
      setOriginalCharacterFeedback([]);
      setFilteredCharacterFeedback([]);
      setGoalSearchModal('');
      setShowEvalFilters(false);
      setShowEvalChartModal(false);
    }
  }, [show, character]); // 依賴 show 和 character

  // --- 監聽目標搜尋變化並執行篩選 ---
  useEffect(() => {
    if (!show) return; // Modal 關閉時不執行

    let result = [...originalCharacterFeedback]; // 從原始數據開始

    if (goalSearchModal.trim()) {
        const lowerCaseGoal = goalSearchModal.trim().toLowerCase();
        result = result.filter(fb =>
            fb.goal?.toLowerCase().includes(lowerCaseGoal)
        );
        console.log(`EvalModal - Filtered by goal "${goalSearchModal}", ${result.length} results.`); // 調試日誌
    } else {
        // 如果搜尋框清空，顯示所有原始數據
        console.log("EvalModal - Goal search cleared, showing all original feedback for character.");
    }

    setFilteredCharacterFeedback(result); // 更新顯示的數據

  }, [originalCharacterFeedback, goalSearchModal, show]); // 依賴原始數據、搜尋詞和顯示狀態

  return (
    <Modal show={show} onHide={onHide} centered size="lg" dialogClassName="character-eval-modal-dialog">
      <Modal.Header closeButton>
        <Modal.Title>與 "{character?.name}" 的對話評估</Modal.Title>
      </Modal.Header>
      <Modal.Body className="d-flex flex-column p-0 character-eval-modal-body">

         {/* --- 可收起的篩選區域 --- */}
         <div className="character-eval-filters bg-light p-3 border-bottom">
           <div className="d-flex justify-content-between align-items-center mb-2">
             <h6 className="mb-0 text-muted small">搜尋對話目標</h6>
             <Button variant="link" size="sm" onClick={() => setShowEvalFilters(!showEvalFilters)} aria-controls="collapse-eval-filters" aria-expanded={showEvalFilters} className="p-0 text-secondary">
               {showEvalFilters ? '收起' : '展開'} <i className={`bi bi-chevron-${showEvalFilters ? 'up' : 'down'}`}></i>
             </Button>
           </div>
           <Collapse in={showEvalFilters}>
             <div id="collapse-eval-filters">
                <InputGroup size="sm" className="mb-2">
                   <InputGroup.Text><i className="bi bi-search"></i></InputGroup.Text>
                   <Form.Control type="text" placeholder="搜尋此角色的對話目標..." value={goalSearchModal} onChange={(e) => setGoalSearchModal(e.target.value)} />
                 </InputGroup>
                 {goalSearchModal && (
                    <Button variant="outline-secondary" size="sm" onClick={() => setGoalSearchModal('')} className="w-100">
                      清除目標搜尋
                    </Button>
                 )}
             </div>
           </Collapse>
         </div>

        {/* --- 可滾動的內容區域 --- */}
        <div className="character-eval-content flex-grow-1 overflow-auto p-3">
          {/* --- 檢查是否有原始數據 --- */}
          {originalCharacterFeedback.length > 0 ? (
            <>
              {/* 圖表按鈕 (只有在篩選後仍有數據時才啟用) */}
              <div className="text-end mb-3">
                   <Button variant="outline-primary" size="sm" onClick={() => setShowEvalChartModal(true)} disabled={filteredCharacterFeedback.length === 0}>
                       <i className="bi bi-graph-up me-1"></i> 查看趨勢圖表
                   </Button>
               </div>

              {/* 歷史記錄列表 (根據篩選結果渲染) */}
              <h5 className="mb-3">詳細回饋記錄 ({filteredCharacterFeedback.length} 筆)</h5>
              {filteredCharacterFeedback.length > 0 ? (
                  <ListGroup variant="flush">
                    {/* 保持從最新到最舊顯示 */}
                    {filteredCharacterFeedback.map(fb => (
                      <ListGroup.Item key={fb.timestamp} className="px-0 d-flex justify-content-between align-items-start">
                        {/* 左側內容 */}
                        <div className="flex-grow-1 me-2">
                          <div className="d-flex justify-content-between align-items-center mb-2">
                            <small className="text-muted">{new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })}</small>
                            <Badge bg="light" text="dark">目標: {fb.goal || '未指定'}</Badge>
                          </div>
                          <h6>AI 總結:</h6>
                          <p style={{ whiteSpace: 'pre-wrap', fontSize: '0.95em' }}>{fb.summary || '-'}</p>
                          {fb.scores && Object.values(fb.scores).some(s => s !== null) && (
                            <>
                              <p className="small mb-1">評分:</p>
                              <ul className="list-inline small mb-0">
                                {ALL_METRICS.map(metric => (
                                  (fb.scores[metric.key] !== null && fb.scores[metric.key] !== undefined) &&
                                  <li key={metric.key} className="list-inline-item me-3">
                                    {metric.label}: <Badge pill bg={getScore(fb.scores[metric.key]) >= 60 ? "success" : (getScore(fb.scores[metric.key]) > 0 ? "warning" : "secondary")}>
                                      {getScore(fb.scores[metric.key]) ?? 'N/A'}
                                    </Badge>
                                  </li>
                                ))}
                              </ul>
                            </>
                          )}
                        </div>
                        {/* 右側刪除按鈕 */}
                        {/* 確保 onDeleteFeedback 存在再渲染按鈕 */}
                        {onDeleteFeedback && (
                            <Button
                               variant="outline-danger"
                               size="sm"
                               className="delete-feedback-btn p-0 flex-shrink-0"
                               onClick={() => onDeleteFeedback(fb.timestamp)} // 調用傳入的刪除函數
                               title="刪除此筆回饋"
                               style={{ lineHeight: 1, width: '24px', height: '24px' }}
                            >
                               <i className="bi bi-x"></i>
                            </Button>
                        )}
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
              ) : (
                 // 有原始數據，但篩選後沒有
                 <Alert variant="warning">找不到符合目標關鍵字 "{goalSearchModal}" 的記錄。</Alert>
              )}
            </>
          ) : (
             // 完全沒有此角色的回饋記錄
             <Alert variant="info">還沒有與此角色的對話評估記錄。</Alert>
          )}
        </div>
      </Modal.Body>
      <Modal.Footer> <Button variant="secondary" onClick={onHide}> 關閉 </Button> </Modal.Footer>

       {/* --- 圖表 Modal --- */}
       <ChartModal
          show={showEvalChartModal}
          onHide={() => setShowEvalChartModal(false)}
          // 傳遞給圖表 Modal 的數據也應該是篩選過的
          feedbackData={filteredCharacterFeedback}
        />

    </Modal>
  );
}

export default CharacterEvalModal;
