// src/components/FeedbackWall/ChartModal.js (新增文件)

import React, { useState, useEffect, useMemo } from 'react';
import { Modal, Button, Row, Col, Form } from 'react-bootstrap';
import FeedbackChart from './FeedbackChart'; // 複用圖表元件
// import DatePicker from 'react-datepicker'; // 引入日期選擇器 (如果要做時間範圍)
// import 'react-datepicker/dist/react-datepicker.css'; // 引入日期選擇器樣式

// 定義所有可選的指標和對應顏色 (從 FeedbackWall 搬過來)
const ALL_METRICS = [
  { key: 'clarity', label: '清晰度', color: 'rgba(54, 162, 235, 0.6)' },
  { key: 'empathy', label: '同理心', color: 'rgba(255, 206, 86, 0.6)' },
  { key: 'confidence', label: '自信', color: 'rgba(75, 192, 192, 0.6)' },
  { key: 'appropriateness', label: '適當性', color: 'rgba(153, 102, 255, 0.6)' },
  { key: 'goalAchievement', label: '目標達成', color: 'rgba(255, 99, 132, 0.6)' },
];

function ChartModal({ show, onHide, feedbackData }) { // 接收篩選後的數據
  // --- 篩選狀態 ---
  // const [dateRange, setDateRange] = useState([null, null]); // [startDate, endDate]
  const [selectedMetrics, setSelectedMetrics] = useState(ALL_METRICS.map(m => m.key)); // 初始全選

  // --- 應用篩選 ---
  const filteredChartData = useMemo(() => {
    let result = [...feedbackData]; // 使用傳入的數據

    // 1. 按日期範圍篩選 (TODO)
    // const [startDate, endDate] = dateRange;
    // if (startDate) { result = result.filter(fb => fb.timestamp >= startDate.getTime()); }
    // if (endDate) { result = result.filter(fb => fb.timestamp <= endDate.getTime() + (24*60*60*1000 - 1)); } // 包含結束日期當天

    // 按時間升序排列，適合圖表
    return result.sort((a, b) => a.timestamp - b.timestamp);

  }, [feedbackData /*, dateRange*/ ]); // 依賴傳入數據和日期範圍

  // --- 準備圖表 Labels 和 Datasets ---
  const chartLabels = filteredChartData.map((fb) => new Date(fb.timestamp).toLocaleDateString('zh-TW', { month: 'numeric', day: 'numeric' }));
  const getScore = (score) => (score === null || score === undefined ? 0 : score);

  const chartDataSets = useMemo(() => {
      return ALL_METRICS
          .filter(metric => selectedMetrics.includes(metric.key)) // 只包含選中的指標
          .map(metric => ({
              label: metric.label,
              data: filteredChartData.map(fb => getScore(fb.scores?.[metric.key])),
              backgroundColor: metric.color,
              borderColor: metric.color.replace('0.6', '1'),
              borderWidth: 1,
              fill: false,
              tension: 0.1
          }));
  }, [filteredChartData, selectedMetrics]);

  // --- 處理指標選擇變化 ---
  const handleMetricChange = (event) => {
    const { value, checked } = event.target;
    setSelectedMetrics(prev =>
      checked ? [...prev, value] : prev.filter(metricKey => metricKey !== value)
    );
  };

  return (
    <Modal show={show} onHide={onHide} centered size="xl"> {/* 使用更大尺寸 */}
      <Modal.Header closeButton>
        <Modal.Title>表現趨勢圖表</Modal.Title>
      </Modal.Header>
      <Modal.Body style={{ minHeight: '500px' }}> {/* 給予最小高度 */}
        {/* --- 篩選控制項 --- */}
        <Row className="mb-3 align-items-center">
          {/* <Col md={4}> // TODO: 日期範圍選擇器
            <Form.Group>
              <Form.Label className="small mb-1">日期範圍:</Form.Label>
              <DatePicker
                selectsRange={true}
                startDate={dateRange[0]}
                endDate={dateRange[1]}
                onChange={(update) => setDateRange(update)}
                isClearable={true}
                dateFormat="yyyy/MM/dd"
                className="form-control form-control-sm"
                placeholderText="選擇開始與結束日期"
              />
            </Form.Group>
          </Col> */}
          <Col md={8}> {/* 佔滿剩餘空間 */}
             <Form.Group>
               <Form.Label className="small mb-1">顯示指標:</Form.Label>
               <div>
                 {ALL_METRICS.map(metric => (
                   <Form.Check
                     key={metric.key}
                     inline
                     type="checkbox"
                     id={`metric-${metric.key}`}
                     label={metric.label}
                     value={metric.key}
                     checked={selectedMetrics.includes(metric.key)}
                     onChange={handleMetricChange}
                     size="sm"
                   />
                 ))}
               </div>
             </Form.Group>
          </Col>
        </Row>
        <hr/>

        {/* --- 圖表 --- */}
        {filteredChartData.length > 0 ? (
             chartDataSets.length > 0 ? ( // 確保有選中的指標
                <FeedbackChart labels={chartLabels} datasets={chartDataSets} />
             ) : (
                <Alert variant="warning" size="sm">請至少選擇一個要顯示的指標。</Alert>
             )
        ) : (
          <Alert variant="info" size="sm">
             {/* {dateRange[0] || dateRange[1] ? '選定日期範圍內沒有回饋數據。' : '沒有回饋數據可供顯示。'} */}
             沒有回饋數據可供顯示。
          </Alert>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          關閉
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default ChartModal;
