// src/components/CharacterHall/CharacterHall.js (添加重複名稱檢查)

import React, { useState } from 'react';
import { Button, Row, Col, Card, Alert } from 'react-bootstrap'; // 保持導入 Alert
import AddCharacterModal from './AddCharacterModal';

function CharacterHall({ characters, onUpdateCharacters, onSelectCharacter }) {
  const [showModal, setShowModal] = useState(false);
  const [addError, setAddError] = useState(null); // 新增狀態來處理錯誤訊息

  const handleAddCharacter = (newCharacter) => {
    setAddError(null); // 清除之前的錯誤訊息

    // --- 添加重複名稱檢查 ---
    const trimmedNewName = newCharacter.name.trim(); // 去除頭尾空格比較
    const isDuplicate = characters.some(
      (character) => character.name.trim().toLowerCase() === trimmedNewName.toLowerCase()
    ); // 不區分大小寫比較

    if (isDuplicate) {
      // 如果名稱重複，設置錯誤訊息，並阻止新增
      setAddError(`角色名稱 "${trimmedNewName}" 已經存在，請使用不同的名稱。`);
      // 不關閉 Modal，讓使用者可以修改
      // setShowModal(false); // 不要關閉
      return; // 終止函數執行
    }
    // --- 結束檢查 ---

    // 如果名稱不重複，繼續執行新增邏輯
    const characterWithId = { ...newCharacter, id: `char_${Date.now()}_${Math.random().toString(36).substring(2, 7)}` };
    const updatedCharacters = [...characters, characterWithId];
    onUpdateCharacters(updatedCharacters);
    setShowModal(false); // 新增成功後關閉 Modal
  };

  const handleDeleteCharacter = (idToDelete) => {
    if (idToDelete === 'default') {
        alert("無法刪除預設助手角色。");
        return;
    }
    if (window.confirm(`確定要刪除角色 "${characters.find(c => c.id === idToDelete)?.name}" 嗎？`)) { // 添加確認提示
       const updatedCharacters = characters.filter(c => c.id !== idToDelete);
       onUpdateCharacters(updatedCharacters);
    }
  };

  // 關閉 Modal 時也清除錯誤訊息
  const handleHideModal = () => {
      setShowModal(false);
      setAddError(null);
  }

  return (
    <div>
      <h2 className="mb-3">角色館</h2>
      <p className="text-muted mb-4">在這裡新增、管理你想互動練習的 AI 角色。選擇一個角色後，點擊「開始訓練」即可進入訓練室。</p>
      <Button variant="primary" onClick={() => setShowModal(true)} className="mb-4">
        <i className="bi bi-plus-lg me-2"></i>新增角色
      </Button>

      {/* 如果有新增錯誤，顯示 Alert */}
      {addError && <Alert variant="danger" onClose={() => setAddError(null)} dismissible>{addError}</Alert>}

      {characters.length === 0 && !addError && ( // 確保沒有錯誤時才顯示這個提示
        <Alert variant="info">目前沒有任何自訂角色，快新增一個吧！</Alert>
      )}

      <Row xs={1} md={2} lg={3} xl={4} className="g-4">
        {characters.map((character) => (
          <Col key={character.id}>
            <Card className="h-100 shadow-sm character-card">
              <Card.Body className="d-flex flex-column">
                <Card.Title>{character.name}</Card.Title>
                <Card.Text className="text-muted small flex-grow-1 character-description">
                  {character.description}
                </Card.Text>
                <div className="mt-auto d-flex justify-content-between align-items-center pt-2">
                  <Button
                    variant="success"
                    size="sm"
                    onClick={() => onSelectCharacter(character)}
                    title="選擇此角色並開始訓練"
                  >
                    <i className="bi bi-chat-dots-fill me-1"></i> 開始訓練
                  </Button>
                  {character.id !== 'default' && (
                     <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() => handleDeleteCharacter(character.id)}
                        title="刪除此角色"
                     >
                       <i className="bi bi-trash3"></i>
                     </Button>
                  )}
                   {character.id === 'default' && ( <span style={{ width: '31px' }}></span> )}
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <AddCharacterModal
        show={showModal}
        onHide={handleHideModal} // 使用新的關閉處理函數
        onAddCharacter={handleAddCharacter}
        // 可以選擇將 addError 傳遞給 Modal 顯示，或者像現在這樣直接在 CharacterHall 顯示
      />

      {/* 樣式保持不變 */}
      <style jsx global>{` /* ... */ `}</style>
    </div>
  );
}

export default CharacterHall;
