// src/components/CharacterHall/CharacterHall.js (實現固定頭部)

import React, { useState } from 'react';
// 導入 Container, Row, Col, Card, Button, BootstrapAlert
import { Button, Row, Col, Card, Alert as BootstrapAlert, Container } from 'react-bootstrap';
import AddCharacterModal from './AddCharacterModal';
// 導入對應的 CSS 文件 (假設你創建了它)
import './CharacterHall.css'; // <<<--- 導入 CharacterHall 的 CSS (如果樣式放在這裡)
// 或者如果你把樣式加到了 App.css，這行可以不加

function CharacterHall({ characters, onUpdateCharacters, onSelectCharacter }) {
  const [showModal, setShowModal] = useState(false);
  // 不再需要 addError 狀態，因為錯誤處理移到了 Modal 內部

  // 處理新增角色請求的函數 (只檢查重複，不處理 UI 提示)
  const handleAddCharacterRequest = (newCharacter) => {
    const trimmedNewName = newCharacter.name.trim();
    const isDuplicate = characters.some(
      (character) => character.name.trim().toLowerCase() === trimmedNewName.toLowerCase()
    );
    if (isDuplicate) {
      console.warn(`重複的角色名稱: "${trimmedNewName}"`);
      return false; // 返回 false 表示重複
    }
    // 新增邏輯
    const characterWithId = { ...newCharacter, name: trimmedNewName, id: `char_${Date.now()}_${Math.random().toString(36).substring(2, 7)}` };
    const updatedCharacters = [...characters, characterWithId];
    onUpdateCharacters(updatedCharacters);
    return true; // 返回 true 表示成功
  };

  // 處理刪除角色
  const handleDeleteCharacter = (idToDelete) => {
    if (idToDelete === 'default') {
        alert("無法刪除預設助手角色。");
        return;
    }
    const characterToDelete = characters.find(c => c.id === idToDelete);
    if (characterToDelete && window.confirm(`確定要刪除角色 "${characterToDelete.name}" 嗎？`)) {
       const updatedCharacters = characters.filter(c => c.id !== idToDelete);
       onUpdateCharacters(updatedCharacters);
    }
  };

  // 處理關閉 Modal
  const handleHideModal = () => {
      setShowModal(false);
  }

  return (
    // --- 使用 Container 包裹，並添加 character-hall-container class ---
    // 添加 p-0 移除 Container 默認 padding，由內部元素控制
    // d-flex flex-column 啟用 Flexbox 垂直佈局
    <Container fluid className="character-hall-container p-0 d-flex flex-column">

      {/* --- 固定的頭部區域 --- */}
      {/* 添加 bg-light 和 shadow-sm 提供視覺區隔 */}
      {/* p-3 提供內邊距 */}
      <div className="character-hall-header bg-light p-3 shadow-sm">
        <h2 className="mb-2">角色館</h2>
        <p className="text-muted mb-3">在這裡新增、管理你想互動練習的 AI 角色。選擇一個角色後，點擊「開始訓練」即可進入訓練室。</p>
        <Button variant="primary" onClick={() => setShowModal(true)}>
          <i className="bi bi-plus-lg me-2"></i>新增角色
        </Button>
      </div>
      {/* --- 結束固定的頭部區域 --- */}


      {/* --- 可滾動的角色卡片區域 --- */}
      {/* 使用 character-hall-body class (在 CSS 中設置 flex-grow 和 overflow) */}
      {/* p-4 提供內邊距 */}
      <div className="character-hall-body p-4">
        {/* 無角色提示 */}
        {characters.filter(c => c.id !== 'default').length === 0 && (
          <BootstrapAlert variant="info">目前沒有任何自訂角色，快新增一個吧！</BootstrapAlert>
        )}

        <Row xs={1} md={2} lg={3} xl={4} className="g-4">
          {characters.map((character) => (
            <Col key={character.id}>
              <Card className="h-100 shadow-sm character-card">
                <Card.Body className="d-flex flex-column">
                  <Card.Title>{character.name}</Card.Title>
                  <Card.Text className="text-muted small flex-grow-1 character-description">
                    {character.description}
                  </Card.Text>
                  <div className="mt-auto d-flex justify-content-between align-items-center pt-2">
                    <Button
                      variant="success"
                      size="sm"
                      onClick={() => onSelectCharacter(character)}
                      title="選擇此角色並開始訓練"
                    >
                      <i className="bi bi-chat-dots-fill me-1"></i> 開始訓練
                    </Button>
                    {character.id !== 'default' && (
                       <Button
                          variant="outline-danger"
                          size="sm"
                          onClick={() => handleDeleteCharacter(character.id)}
                          title="刪除此角色"
                       >
                         <i className="bi bi-trash3"></i>
                       </Button>
                    )}
                    {character.id === 'default' && ( <span style={{ width: '31px' }}></span> )}
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </div>
      {/* --- 結束可滾動的角色卡片區域 --- */}


      <AddCharacterModal
        show={showModal}
        onHide={handleHideModal}
        // 確保傳遞的是檢查函數
        onAddCharacterRequest={handleAddCharacterRequest}
      />

      {/* 移除 style jsx global */}
    </Container>
  );
}

export default CharacterHall;
