// src/components/CharacterHall/CharacterEvalModal.js (再次修正和優化)

import React, { useState, useEffect, useMemo } from 'react'; // 移除 useCallback
import { Modal, Button, Alert, ListGroup, Badge, Collapse, Form, InputGroup, Row, Col } from 'react-bootstrap';
import ChartModal from '../FeedbackWall/ChartModal';
import AverageRadarChart from '../FeedbackWall/AverageRadarChart';
import './CharacterHall.css';

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';
const ALL_METRICS = [ { key: 'clarity', label: '清晰度' }, { key: 'empathy', label: '同理心' }, { key: 'confidence', label: '自信' }, { key: 'appropriateness', label: '適當性' }, { key: 'goalAchievement', label: '目標達成' }, ];
const getScore = (score) => (score === null || score === undefined ? 0 : score);

function CharacterEvalModal({ show, onHide, character }) {
  // 只保留最終顯示的狀態
  const [displayFeedback, setDisplayFeedback] = useState([]);
  const [goalSearchModal, setGoalSearchModal] = useState('');
  const [showEvalFilters, setShowEvalFilters] = useState(false);
  const [showEvalChartModal, setShowEvalChartModal] = useState(false);
  const [showEvalRadarModal, setShowEvalRadarModal] = useState(false);
  const [hasRecordsInitially, setHasRecordsInitially] = useState(false); // 新增狀態判斷初始是否有記錄

  // --- 修改：合併加載和篩選邏輯到一個 useEffect ---
  useEffect(() => {
    if (show && character) {
      console.log(`EvalModal - Loading & Filtering feedback for: ${character.name}`);
      const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
      let allFeedback = [];
      if (savedFeedback) {
        try {
          allFeedback = JSON.parse(savedFeedback);
          if (!Array.isArray(allFeedback)) allFeedback = [];
        } catch (e) { console.error("EvalModal - Parse error", e); allFeedback = []; }
      }

      // 1. 過濾出當前角色的記錄
      let characterFeedback = allFeedback.filter(fb =>
          fb.characterId === character.id && typeof fb.timestamp === 'number'
      );

      // 記錄初始是否有數據
      setHasRecordsInitially(characterFeedback.length > 0);
      console.log(`EvalModal - Initial records found: ${characterFeedback.length}`);

      // 2. 根據目標篩選
      if (goalSearchModal.trim()) {
        const lowerCaseGoal = goalSearchModal.trim().toLowerCase();
        characterFeedback = characterFeedback.filter(fb =>
            fb.goal?.toLowerCase().includes(lowerCaseGoal)
        );
         console.log(`EvalModal - Filtered by goal "${goalSearchModal}", ${characterFeedback.length} results.`);
      }

      // 3. 按時間降序排序
      characterFeedback.sort((a, b) => b.timestamp - a.timestamp);

      // 4. 更新顯示狀態
      setDisplayFeedback(characterFeedback);

      // 重置 Modal 內狀態 (除了搜尋詞)
      setShowEvalFilters(false);
      setShowEvalChartModal(false);
      setShowEvalRadarModal(false);

    } else {
      // Modal 關閉或無角色時清空
      setDisplayFeedback([]);
      setGoalSearchModal('');
      setHasRecordsInitially(false);
    }
  }, [show, character, goalSearchModal]); // 依賴這些變化

  // --- 修改：刪除回饋處理函數 ---
  const handleDeleteFeedbackInModal = (timestampToDelete) => {
    const feedbackToDelete = displayFeedback.find(fb => fb.timestamp === timestampToDelete);
    if (feedbackToDelete && window.confirm(`確定要刪除這筆 ${new Date(timestampToDelete).toLocaleString('zh-TW')} 的回饋記錄嗎？\n目標：${feedbackToDelete.goal}`)) {
      try {
        const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
        let allFeedback = [];
        if (savedFeedback) { allFeedback = JSON.parse(savedFeedback); if (!Array.isArray(allFeedback)) allFeedback = []; }

        const updatedAllHistory = allFeedback.filter(fb => fb.timestamp !== timestampToDelete);
        localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify(updatedAllHistory));
        console.log(`EvalModal - Deleted feedback: ${timestampToDelete}`);

        // --- 直接更新當前顯示的狀態 ---
        setDisplayFeedback(prev => prev.filter(fb => fb.timestamp !== timestampToDelete));
        // --- 同時更新初始是否有記錄的狀態 (如果刪除的是最後一條) ---
        if (originalFeedbackHistory.length === 1 && originalFeedbackHistory[0].timestamp === timestampToDelete) {
             setHasRecordsInitially(false);
        }

      } catch (e) { console.error("Error deleting feedback", e); alert("刪除回饋時發生錯誤。"); }
    }
  }

  // --- 計算平均分數 (基於 displayFeedback) ---
  const averageScores = useMemo(() => { const scoreSums = { clarity: 0, empathy: 0, confidence: 0, appropriateness: 0, goalAchievement: 0 }; const scoreCounts = { clarity: 0, empathy: 0, confidence: 0, appropriateness: 0, goalAchievement: 0 }; displayFeedback.forEach(fb => { if (fb.scores) { ALL_METRICS.forEach(metric => { const score = fb.scores[metric.key]; if (score !== null && score !== undefined) { scoreSums[metric.key] += score; scoreCounts[metric.key]++; } }); } }); const averages = {}; ALL_METRICS.forEach(metric => { averages[metric.key] = scoreCounts[metric.key] > 0 ? scoreSums[metric.key] / scoreCounts[metric.key] : 0; }); return averages; }, [displayFeedback]);

  // 清除 Modal 內的篩選
  const clearModalFilters = () => { setGoalSearchModal(''); };

  return (
    <Modal show={show} onHide={onHide} centered size="lg" dialogClassName="character-eval-modal-dialog">
      <Modal.Header closeButton> <Modal.Title>與 "{character?.name}" 的對話評估</Modal.Title> </Modal.Header>
      <Modal.Body className="d-flex flex-column p-0 character-eval-modal-body">
         {/* 可收起的篩選區域 */}
         <div className="character-eval-filters bg-light p-3 border-bottom">
           <div className="d-flex justify-content-between align-items-center mb-2"> <h6 className="mb-0 text-muted small">搜尋對話目標</h6> <Button variant="link" size="sm" onClick={() => setShowEvalFilters(!showEvalFilters)} aria-controls="collapse-eval-filters" aria-expanded={showEvalFilters} className="p-0 text-secondary"> {showEvalFilters ? '收起' : '展開'} <i className={`bi bi-chevron-${showEvalFilters ? 'up' : 'down'}`}></i> </Button> </div>
           <Collapse in={showEvalFilters}>
             <div id="collapse-eval-filters">
                <InputGroup size="sm" className="mb-2"> <InputGroup.Text><i className="bi bi-search"></i></InputGroup.Text> <Form.Control type="text" placeholder="搜尋此角色的對話目標..." value={goalSearchModal} onChange={(e) => setGoalSearchModal(e.target.value)} /> </InputGroup>
                 {goalSearchModal && ( <Button variant="outline-secondary" size="sm" onClick={clearModalFilters} className="w-100"> 清除目標搜尋 </Button> )}
             </div>
           </Collapse>
         </div>
         {/* 固定的圖表按鈕區域 */}
         <div className="character-eval-chart-controls p-3 border-bottom">
             <div className="d-flex justify-content-end">
                 <Button variant="outline-success" size="sm" onClick={() => setShowEvalRadarModal(true)} disabled={displayFeedback.length === 0} className="me-2"> <i className="bi bi-bullseye me-1"></i> 查看平均雷達圖 </Button>
                 <Button variant="outline-primary" size="sm" onClick={() => setShowEvalChartModal(true)} disabled={displayFeedback.length === 0}> <i className="bi bi-graph-up me-1"></i> 查看趨勢圖表 </Button>
             </div>
         </div>
        {/* 可滾動的內容區域 */}
        <div className="character-eval-content flex-grow-1 overflow-auto p-3">
          {/* --- 修改判斷邏輯 --- */}
          {!hasRecordsInitially ? ( // <<<--- 先用初始是否有記錄的狀態判斷
             <Alert variant="info">還沒有與此角色的對話評估記錄。</Alert>
          ) : filteredFeedbackHistory.length > 0 ? ( // 如果初始有記錄，再看篩選後是否有
              <>
                <h5 className="mb-3">詳細回饋記錄 ({filteredFeedbackHistory.length} 筆)</h5>
                <ListGroup variant="flush">
                  {filteredFeedbackHistory.map(fb => (
                    <ListGroup.Item key={fb.timestamp} className="px-0 d-flex justify-content-between align-items-start">
                      {/* 左側內容 */}
                      <div className="flex-grow-1 me-2">
                        <div className="d-flex justify-content-between align-items-center mb-2"> <small className="text-muted">{new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })}</small> <Badge bg="light" text="dark">目標: {fb.goal || '未指定'}</Badge> </div> <h6>AI 總結:</h6> <p style={{ whiteSpace: 'pre-wrap', fontSize: '0.95em' }}>{fb.summary || '-'}</p>
                        {fb.scores && ( <> <p className="small mb-1">評分:</p> <ul className="list-inline small mb-0"> {ALL_METRICS.map(metric => { const scoreValue = fb.scores[metric.key]; const displayValue = (scoreValue === null || scoreValue === undefined) ? 'N/A' : getScore(scoreValue); const badgeBg = displayValue === 'N/A' ? 'light' : (getScore(scoreValue) >= 60 ? "success" : "warning"); return ( <li key={metric.key} className="list-inline-item me-3"> {metric.label}: <Badge pill bg={badgeBg} text={displayValue === 'N/A' ? 'dark' : undefined}> {displayValue} </Badge> </li> ); })} </ul> </> )}
                      </div>
                      {/* 右側刪除按鈕 */}
                      <Button variant="outline-danger" size="sm" className="delete-feedback-btn p-0 flex-shrink-0" onClick={() => handleDeleteFeedbackInModal(fb.timestamp)} title="刪除此筆回饋" style={{ lineHeight: 1, width: '24px', height: '24px' }}> <i className="bi bi-x"></i> </Button>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              </>
            ) : (
               // 初始有記錄，但篩選後沒有
               <Alert variant="warning">找不到符合目標關鍵字 "{goalSearchModal}" 的記錄。</Alert>
            )
          }
          {/* --- 結束修改 --- */}
        </div>
      </Modal.Body>
      <Modal.Footer> <Button variant="secondary" onClick={onHide}> 關閉 </Button> </Modal.Footer>
       {/* 圖表 Modals */}
       <ChartModal show={showEvalChartModal} onHide={() => setShowEvalChartModal(false)} feedbackData={filteredCharacterFeedback}/>
       <Modal show={showEvalRadarModal} onHide={() => setShowEvalRadarModal(false)} centered>
         {/* ... (雷達圖 Modal 內容不變) ... */}
       </Modal>
    </Modal>
  );
}

export default CharacterEvalModal;
