// src/components/CharacterHall/CharacterEvalModal.js (修正數據顯示邏輯)

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Modal, Button, Alert, ListGroup, Badge, Collapse, Form, InputGroup, Row, Col } from 'react-bootstrap';
import ChartModal from '../FeedbackWall/ChartModal';
import AverageRadarChart from '../FeedbackWall/AverageRadarChart';
import './CharacterHall.css'; // 確保導入 CSS

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';
const ALL_METRICS = [ { key: 'clarity', label: '清晰度' }, { key: 'empathy', label: '同理心' }, { key: 'confidence', label: '自信' }, { key: 'appropriateness', label: '適當性' }, { key: 'goalAchievement', label: '目標達成' }, ];
const getScore = (score) => (score === null || score === undefined ? 0 : score);

// --- 保持傳入 props ---
function CharacterEvalModal({ show, onHide, character }) {
  // --- 恢復 originalCharacterFeedback 狀態 ---
  const [originalCharacterFeedback, setOriginalCharacterFeedback] = useState([]); // <<<--- 存儲從 localStorage 加載的原始數據
  const [filteredCharacterFeedback, setFilteredCharacterFeedback] = useState([]); // 存儲篩選後的數據
  const [goalSearchModal, setGoalSearchModal] = useState('');
  const [showEvalFilters, setShowEvalFilters] = useState(false);
  const [showEvalChartModal, setShowEvalChartModal] = useState(false);
  const [showEvalRadarModal, setShowEvalRadarModal] = useState(false);

  // --- 修改：加載原始數據函數 ---
  const loadOriginalFeedback = useCallback(() => {
    if (show && character) {
      console.log(`EvalModal - Loading original feedback for character: ${character.name} (ID: ${character.id})`);
      const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
      let allFeedback = [];
      if (savedFeedback) {
        try {
          allFeedback = JSON.parse(savedFeedback);
          if (!Array.isArray(allFeedback)) allFeedback = [];
        } catch (e) { console.error("EvalModal - Failed to parse feedback history", e); allFeedback = []; }
      } else {
        console.log("EvalModal - No feedback history found in localStorage.");
      }

      // 篩選出屬於當前角色的回饋
      const characterFeedback = allFeedback
        .filter(fb => fb.characterId === character.id && typeof fb.timestamp === 'number')
        .sort((a, b) => b.timestamp - a.timestamp); // 按時間降序存儲

      console.log(`EvalModal - Found ${characterFeedback.length} original records.`);
      setOriginalCharacterFeedback(characterFeedback); // <<<--- 更新原始數據狀態

      // 重置篩選和顯示狀態
      setGoalSearchModal('');
      setShowEvalFilters(false);
      setShowEvalChartModal(false);
      setShowEvalRadarModal(false);

    } else {
      setOriginalCharacterFeedback([]); // 清空原始數據
    }
  }, [show, character]); // 依賴 show 和 character

  // --- 監聽 show 和 character 變化以加載原始數據 ---
  useEffect(() => {
    loadOriginalFeedback();
  }, [loadOriginalFeedback]); // 依賴 useCallback 包裹的函數

  // --- 監聽原始數據和篩選條件，更新 filteredFeedbackHistory ---
  useEffect(() => {
    let result = [...originalCharacterFeedback]; // 從原始數據開始篩選
    if (goalSearchModal.trim()) {
      const lowerCaseGoal = goalSearchModal.trim().toLowerCase();
      result = result.filter(fb =>
          fb.goal?.toLowerCase().includes(lowerCaseGoal)
      );
      console.log(`EvalModal - Filtered by goal "${goalSearchModal}", ${result.length} results.`);
    } else {
      console.log("EvalModal - Goal search cleared, using original data for filtering.");
    }
    setFilteredCharacterFeedback(result); // 更新顯示用的狀態
  }, [originalCharacterFeedback, goalSearchModal]); // 依賴原始數據和目標搜尋詞


  // --- 刪除回饋處理函數 (操作 localStorage 後重新加載原始數據) ---
  const handleDeleteFeedbackInModal = (timestampToDelete) => {
    const feedbackToDelete = originalCharacterFeedback.find(fb => fb.timestamp === timestampToDelete); // 從原始數據找
    if (feedbackToDelete && window.confirm(`確定要刪除這筆 ${new Date(timestampToDelete).toLocaleString('zh-TW')} 的回饋記錄嗎？\n目標：${feedbackToDelete.goal}`)) {
      try {
        const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
        let allFeedback = [];
        if (savedFeedback) { allFeedback = JSON.parse(savedFeedback); if (!Array.isArray(allFeedback)) allFeedback = []; }
        const updatedAllHistory = allFeedback.filter(fb => fb.timestamp !== timestampToDelete);
        localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify(updatedAllHistory));
        console.log(`EvalModal - Deleted feedback with timestamp: ${timestampToDelete}`);
        // --- 關鍵修改：重新加載原始數據來觸發界面更新 ---
        loadOriginalFeedback(); // <<<--- 調用加載函數
      } catch (e) { console.error("Error deleting feedback from localStorage", e); alert("刪除回饋時發生錯誤。"); }
    }
  }

  // --- 計算平均分數 (基於篩選後的數據) ---
  const averageScores = useMemo(() => { /* ... (保持不變) ... */ }, [filteredCharacterFeedback]);

  // 清除 Modal 內的篩選
  const clearModalFilters = () => { setGoalSearchModal(''); };

  return (
    <Modal show={show} onHide={onHide} centered size="lg" dialogClassName="character-eval-modal-dialog">
      <Modal.Header closeButton> <Modal.Title>與 "{character?.name}" 的對話評估</Modal.Title> </Modal.Header>
      <Modal.Body className="d-flex flex-column p-0 character-eval-modal-body">
         {/* 可收起的篩選區域 */}
         <div className="character-eval-filters bg-light p-3 border-bottom">
           {/* ... (篩選器內容保持不變) ... */}
         </div>
        {/* 可滾動的內容區域 */}
        <div className="character-eval-content flex-grow-1 overflow-auto p-3">
          {/* --- 修改這裡的判斷邏輯 --- */}
          {originalCharacterFeedback.length > 0 ? ( // <<<--- 使用 originalCharacterFeedback 判斷是否有數據
            <>
              {/* 圖表按鈕 */}
              <div className="text-end mb-3 d-flex justify-content-end">
                   <Button variant="outline-success" size="sm" onClick={() => setShowEvalRadarModal(true)} disabled={filteredCharacterFeedback.length === 0} className="me-2"> <i className="bi bi-bullseye me-1"></i> 查看平均雷達圖 </Button>
                   <Button variant="outline-primary" size="sm" onClick={() => setShowEvalChartModal(true)} disabled={filteredCharacterFeedback.length === 0}> <i className="bi bi-graph-up me-1"></i> 查看趨勢圖表 </Button>
               </div>
              {/* 歷史記錄列表 */}
              <h5 className="mb-3">詳細回饋記錄 ({filteredCharacterFeedback.length} 筆)</h5>
              {filteredCharacterFeedback.length > 0 ? ( // <<<--- 使用 filtered 判斷是否有可顯示的記錄
                  <ListGroup variant="flush">
                    {filteredCharacterFeedback.map(fb => ( // 渲染 filtered 數據
                      <ListGroup.Item key={fb.timestamp} className="px-0 d-flex justify-content-between align-items-start">
                        {/* 左側內容 */}
                        <div className="flex-grow-1 me-2">
                          {/* ... (渲染時間戳、目標、總結、分數) ... */}
                        </div>
                        {/* 右側刪除按鈕 */}
                        <Button variant="outline-danger" size="sm" className="delete-feedback-btn p-0 flex-shrink-0" onClick={() => handleDeleteFeedbackInModal(fb.timestamp)} title="刪除此筆回饋" style={{ lineHeight: 1, width: '24px', height: '24px' }}> <i className="bi bi-x"></i> </Button>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
              ) : (
                 // 如果 original 有數據，但 filtered 沒有，說明是被篩選掉了
                 <Alert variant="warning">找不到符合目標關鍵字 "{goalSearchModal}" 的記錄。</Alert>
              )}
            </>
          ) : (
             // 如果 originalCharacterFeedback 就為空
             <Alert variant="info">還沒有與此角色的對話評估記錄。</Alert>
          )}
          {/* --- 結束修改 --- */}
        </div>
      </Modal.Body>
      <Modal.Footer> <Button variant="secondary" onClick={onHide}> 關閉 </Button> </Modal.Footer>
       {/* 圖表 Modals */}
       <ChartModal show={showEvalChartModal} onHide={() => setShowEvalChartModal(false)} feedbackData={filteredCharacterFeedback}/>
       <Modal show={showEvalRadarModal} onHide={() => setShowEvalRadarModal(false)} centered>
         {/* ... (雷達圖 Modal 內容不變) ... */}
       </Modal>
    </Modal>
  );
}

export default CharacterEvalModal;
