      }
  }

  // ÁôºÈÄÅË®äÊÅØ
  const handleSendMessage = async () => { if (!currentMessage.trim() || !selectedCharacter) return; if (!goal.trim()) { setError("Ë´ãÂÖàË®≠ÂÆöÊú¨Ê¨°ËÅäÂ§©ÁöÑÁõÆÊ®ôÔºÅ"); return; } if (isLoading || isGettingFeedback) return; const newUserMessage = { id: Date.now(), sender: 'user', text: currentMessage }; const currentInput = currentMessage; setMessages(prevMessages => [...prevMessages, newUserMessage]); setCurrentMessage(''); setIsLoading(true); setError(null); try { const historyForOllama = messages.map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text })); const requestHistory = [...historyForOllama, { role: 'user', content: currentInput }]; const chatModel = process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'llama3.2:latest'; const aiResponseText = await sendMessageToOllama(goal, requestHistory, selectedCharacter, chatModel); const newAiMessage = { id: Date.now() + 1, sender: 'ai', text: aiResponseText }; setMessages(prevMessages => [...prevMessages, newAiMessage]); } catch (err) { console.error("Error contacting Ollama:", err); const errorMsg = err.message || "Ëàá AI ÈÄöË®äÊôÇÁôºÁîüÈåØË™§„ÄÇ"; setError(errorMsg); const errorAiMessage = { id: Date.now() + 1, sender: 'ai', text: `ü§ñ Êä±Ê≠âÔºåËôïÁêÜÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}` }; setMessages(prevMessages => [...prevMessages, errorAiMessage]); } finally { setIsLoading(false); } };

  // ÂõûÈ•ãËß£Êûê
  const parseFeedback = (feedbackText) => { let summary = "ÁÑ°Ê≥ïËß£ÊûêÂõûÈ•ãÊëòË¶Å„ÄÇ"; let scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null }; let summaryMatch = null; let summaryOnlyMatch = null; let scoresTextMatch = null; try { summaryMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*?)\s*\[Scores\]/); if (summaryMatch && summaryMatch[1]) { summary = summaryMatch[1].trim(); } else { summaryOnlyMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*)/); if (summaryOnlyMatch && summaryOnlyMatch[1]) { summary = summaryOnlyMatch[1].trim(); console.warn("Only found summary..."); } else { console.warn("Could not find [Feedback Summary] section."); summary = feedbackText; } } scoresTextMatch = feedbackText.match(/\[Scores\]\s*([\s\S]*)/); if (scoresTextMatch && scoresTextMatch[1]) { const scoreLines = scoresTextMatch[1].trim().split('\n'); scoreLines.forEach(line => { const parts = line.split(':'); if (parts.length === 2) { let key = parts[0].trim().toLowerCase().replace(/\s+/g, ''); if (key === 'goalachievement') key = 'goalAchievement'; const value = parts[1].trim(); if (Object.prototype.hasOwnProperty.call(scores, key)) { if (value.toLowerCase() === 'n/a') { scores[key] = null; } else { const score = parseInt(value, 10); if (!isNaN(score)) { scores[key] = Math.max(0, Math.min(100, score)); } else { console.warn(`Invalid score value "${value}" for key "${key}"`); scores[key] = null; } } } else { console.warn(`Unexpected score key "${key}" found.`); } } }); } else if (!summaryOnlyMatch && summaryMatch === null){ console.warn("Could not find [Scores] section..."); } } catch (e) { console.error("Error parsing feedback text:", e); summary = feedbackText; scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null }; } console.log("Parsed Feedback:", { summary, scores }); return { summary, scores }; };

  // Áç≤ÂèñÂõûÈ•ã‰∏¶Ë®òÈåÑ‰∏ä‰∏ãÊñá
  const handleGetFeedback = async () => {
      if (!selectedCharacter || !goal.trim() || messages.filter(m => m.sender === 'user').length === 0) { alert("Ë´ãÂÖàË®≠ÂÆöÁõÆÊ®ô„ÄÅÈÅ∏ÊìáËßíËâ≤‰∏¶ÈÄ≤Ë°åËá≥Â∞ë‰∏ÄËº™Â∞çË©± (ÂåÖÂê´‰Ω†ÁöÑÁôºË®Ä)ÔºåÊâçËÉΩË´ãÊ±ÇÂõûÈ•ã„ÄÇ"); return; }
      if (isLoading || isGettingFeedback) return;
      setIsGettingFeedback(true); setError(null); const feedbackRequestMessage = { id: Date.now(), sender: 'system', text: '‚è≥ Ê≠£Âú®Âêë AI Ë´ãÊ±ÇÂ∞çË©±ÂõûÈ•ã...' }; setMessages(prevMessages => [...prevMessages, feedbackRequestMessage]);
      try {
          const historyForFeedback = messages.filter(msg => msg.sender !== 'system').map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text }));
          // --- Áç≤ÂèñÁî®Êà∂Ë®äÊÅØ‰∏ä‰∏ãÊñá ---
          const userMessagesForContext = messages.filter(msg => msg.sender === 'user').slice(-3).map(msg => msg.text);
          const feedbackModel = process.env.REACT_APP_OLLAMA_FEEDBACK_MODEL || process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'llama3.2:latest';
          const feedbackRawText = await getFeedbackFromOllama(goal, historyForFeedback, selectedCharacter, feedbackModel);
          const { summary, scores } = parseFeedback(feedbackRawText);
          // ËÅäÂ§©Ê°Ü‰∏≠ÁöÑÂõûÈ•ãË®äÊÅØÔºà‰∏çÈ°ØÁ§∫‰∏ä‰∏ãÊñáÔºâ
          const feedbackMessage = { id: Date.now() + 1, sender: 'ai', isFeedback: true, text: `üí° **Êú¨Ê¨°Â∞çË©±ÂõûÈ•ã (${goal})**\n\n**ÊëòË¶Å:**\n${summary}\n\n**Ë©ïÂàÜ (0-100):**\n- Ê∏ÖÊô∞Â∫¶: ${scores.clarity ?? 'N/A'}\n- ÂêåÁêÜÂøÉ: ${scores.empathy ?? 'N/A'}\n- Ëá™‰ø°: ${scores.confidence ?? 'N/A'}\n- ÈÅ©Áï∂ÊÄß: ${scores.appropriateness ?? 'N/A'}\n- ÁõÆÊ®ôÈÅîÊàê: ${scores.goalAchievement ?? 'N/A'}` };
          // Â≠òÂÑ≤Âà∞ localStorage ÁöÑÊï∏ÊìöÔºàÂåÖÂê´‰∏ä‰∏ãÊñáÔºâ
          const newFeedbackEntry = { timestamp: Date.now(), goal: goal, characterId: selectedCharacter.id, characterName: selectedCharacter.name, scores: scores, summary: summary, userMessagesContext: userMessagesForContext, rawFeedback: feedbackRawText };
          const existingFeedback = JSON.parse(localStorage.getItem(FEEDBACK_STORAGE_KEY) || '[]');
          localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify([...existingFeedback, newFeedbackEntry]));
          setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), feedbackMessage]);
      } catch (err) { console.error("Error getting or parsing feedback from Ollama:", err); const errorMsg = err.message || "ÁÑ°Ê≥ïÁç≤ÂèñÊàñËôïÁêÜ AI ÂõûÈ•ã„ÄÇ"; setError(errorMsg); const errorFeedbackMessage = { id: Date.now() + 1, sender: 'ai', text: `ü§ñ Êä±Ê≠âÔºåÁç≤ÂèñÂõûÈ•ãÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}` }; setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), errorFeedbackMessage]); }
      finally { setIsGettingFeedback(false); }
  };

  return (
    <Container fluid className="training-room-container p-0">
      {/* È†ÇÈÉ®Ê¨Ñ */}
      <Row className="goal-input-row m-0 align-items-center">
        <Col md={7} className="mb-2 mb-md-0">
          <InputGroup>
            <InputGroup.Text>üéØ</InputGroup.Text>
            <Form.Control type="text" placeholder="Ëº∏ÂÖ•Êú¨Ê¨°Á§æ‰∫§Ë®ìÁ∑¥ÁõÆÊ®ô..." value={goal} onChange={(e) => setGoal(e.target.value)} disabled={isLoading || isGettingFeedback} aria-label="ËÅäÂ§©ÁõÆÊ®ô"/>
          </InputGroup>
        </Col>
        <Col md={5} className="d-flex align-items-center justify-content-md-end">
          {characters && characters.length > 0 ? (
            <Dropdown className="me-3" id="character-selector-dropdown">
              <Dropdown.Toggle variant="outline-secondary" size="sm" id="dropdown-character-select-button" disabled={isLoading || isGettingFeedback} style={{minWidth: '120px', textAlign: 'left'}}>
                Ëàá: {selectedCharacter ? <strong className="text-primary">{selectedCharacter.name}</strong> : 'ÈÅ∏ÊìáËßíËâ≤'}
              </Dropdown.Toggle>
              <Dropdown.Menu style={{maxHeight: '300px', overflowY: 'auto'}}>
                {characters.map(character => ( <Dropdown.Item key={character.id} active={selectedCharacter?.id === character.id} onClick={() => onSelectCharacter(character)} disabled={selectedCharacter?.id === character.id}> {character.name} </Dropdown.Item> ))}
