// src/components/FeedbackWall/FeedbackWall.js (修改評分顯示邏輯)

import React, { useState, useEffect, useMemo } from 'react';
import { Container, Row, Col, Card, Alert, Badge, InputGroup, Form, Button, Collapse } from 'react-bootstrap';
import ChartModal from './ChartModal';
// 不再直接在此文件使用 FeedbackChart
// import FeedbackChart from './FeedbackChart';
import './FeedbackWall.css';

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';
// 將指標定義移到這裡，方便列表渲染使用
const ALL_METRICS = [
  { key: 'clarity', label: '清晰度' },
  { key: 'empathy', label: '同理心' },
  { key: 'confidence', label: '自信' },
  { key: 'appropriateness', label: '適當性' },
  { key: 'goalAchievement', label: '目標達成' },
];

function FeedbackWall() {
  // ... (useState, useEffect, clearFilters 保持不變) ...
   const [originalFeedbackHistory, setOriginalFeedbackHistory] = useState([]);
   const [filteredFeedbackHistory, setFilteredFeedbackHistory] = useState([]);
   const [characterSearch, setCharacterSearch] = useState('');
   const [goalSearch, setGoalSearch] = useState('');
   const [showFilters, setShowFilters] = useState(true);
   const [showChartModal, setShowChartModal] = useState(false);
   useEffect(() => { /* ... 加載 ... */ }, []);
   useEffect(() => { /* ... 篩選 ... */ }, [originalFeedbackHistory, characterSearch, goalSearch]);
   const clearFilters = () => { setCharacterSearch(''); setGoalSearch(''); };


  // 提取分數輔助函數 (返回數字或 'N/A')
  const getScoreDisplay = (score) => {
      if (score === null || score === undefined) {
          return 'N/A'; // 如果是 null 或 undefined，顯示 N/A
      }
      return score; // 否則顯示數字分數 (包括 0)
  };
  // 判斷 Badge 顏色的輔助函數 (只對數字分數有效)
  const getScoreBg = (score) => {
      if (typeof score === 'number') {
          return score >= 60 ? "success" : "warning";
      }
      return "secondary"; // N/A 或其他情況用灰色
  }


  return (
    <Container fluid className="feedback-wall-container p-0 d-flex flex-column">
      {/* --- 固定的頭部區域 (保持不變) --- */}
      <div className="feedback-wall-header d-flex justify-content-between align-items-center">
         {/* ... (標題和圖表按鈕) ... */}
          <div> <h2 className="mb-1">回饋牆</h2> <p className="text-muted mb-0 small">這裡彙總了你每次請求 AI 回饋的結果。</p> </div> <Button variant="outline-primary" size="sm" onClick={() => setShowChartModal(true)} disabled={filteredFeedbackHistory.length === 0}> <i className="bi bi-graph-up me-1"></i> 查看趨勢圖表 </Button>
      </div>

      {/* --- 可收起的篩選控制項區域 (保持不變) --- */}
      <div className="feedback-filters-container">
        {/* ... (收起/展開按鈕和表單) ... */}
         <div className="d-flex justify-content-between align-items-center mb-2"> <h6 className="mb-0 text-muted small">搜尋與篩選</h6> <Button variant="link" size="sm" onClick={() => setShowFilters(!showFilters)} aria-controls="collapse-feedback-filters" aria-expanded={showFilters} className="p-0 text-secondary"> {showFilters ? '收起' : '展開'} <i className={`bi bi-chevron-${showFilters ? 'up' : 'down'}`}></i> </Button> </div> <Collapse in={showFilters}> <div id="collapse-feedback-filters"> <Row className="g-2 mb-2"> <Col md={6}> <InputGroup size="sm"> <InputGroup.Text><i className="bi bi-person"></i></InputGroup.Text> <Form.Control type="text" placeholder="搜尋角色名稱..." value={characterSearch} onChange={(e) => setCharacterSearch(e.target.value)}/> </InputGroup> </Col> <Col md={6}> <InputGroup size="sm"> <InputGroup.Text><i className="bi bi-chat-dots"></i></InputGroup.Text> <Form.Control type="text" placeholder="搜尋對話目標關鍵字..." value={goalSearch} onChange={(e) => setGoalSearch(e.target.value)}/> </InputGroup> </Col> </Row> {(characterSearch || goalSearch) && ( <Button variant="outline-secondary" size="sm" onClick={clearFilters} className="w-100"><i className="bi bi-x-lg"></i> 清除所有篩選</Button> )} </div> </Collapse>
      </div>

      {/* --- 可滾動的內容區域 --- */}
      <div className="feedback-wall-body">
        <h4 className="h5 mb-3">詳細回饋記錄 ({filteredFeedbackHistory.length} 筆)</h4>
        {filteredFeedbackHistory.length > 0 ? (
          filteredFeedbackHistory.map((fb) => (
            <Card key={fb.timestamp} className="shadow-sm">
              <Card.Header className="d-flex justify-content-between align-items-center small text-muted flex-wrap">
                <span>{new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })}</span>
                <div className="mt-1 mt-md-0">
                  <Badge bg="secondary" className="me-2">角色: {fb.characterName || '未知'}</Badge>
                  <Badge bg="info" text="dark">目標: {fb.goal || '未知'}</Badge>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Subtitle className="mb-2 text-muted">AI 總結:</Card.Subtitle>
                <p style={{ whiteSpace: 'pre-wrap' }}>{fb.summary || '-'}</p>
                {/* --- 修改這裡：始終渲染所有指標，處理 N/A --- */}
                {fb.scores && ( // 確保 scores 對象存在
                  <>
                    <hr />
                    <p className="small mb-1">評分細節:</p>
                    <ul className="list-inline small mb-0">
                      {ALL_METRICS.map(metric => {
                        const scoreValue = fb.scores[metric.key]; // 獲取分數值 (可能是數字, null, undefined)
                        const displayScore = getScoreDisplay(scoreValue); // 獲取顯示文本 ('N/A' 或數字)
                        const badgeBg = getScoreBg(scoreValue); // 獲取 Badge 背景色
                        return (
                          <li key={metric.key} className="list-inline-item me-3">
                            {metric.label}: <Badge pill bg={badgeBg}>
                              {displayScore}
                            </Badge>
                          </li>
                        );
                      })}
                    </ul>
                  </>
                )}
                 {/* 如果連 scores 對象都沒有，可以顯示提示 */}
                 {!fb.scores && <p className="small text-muted fst-italic">沒有評分數據。</p>}
                {/* --- 結束修改 --- */}
              </Card.Body>
            </Card>
          ))
        ) : (
          <Alert variant="secondary" className="text-center">
             {characterSearch || goalSearch ? '找不到符合篩選條件的回饋記錄。' : '還沒有任何回饋記錄。'}
          </Alert>
        )}
      </div>

      {/* 圖表 Modal */}
      <ChartModal
         show={showChartModal}
         onHide={() => setShowChartModal(false)}
         feedbackData={filteredFeedbackHistory}
       />
    </Container>
  );
}

export default FeedbackWall;
