// src/components/TrainingRoom/MessageInput.js (Ë™øÊï¥Â∞çÈΩä)

import React, { useRef } from 'react';
import { Form, Button, InputGroup, Spinner } from 'react-bootstrap';

function MessageInput({
    currentMessage,
    onMessageChange,
    onSendMessage,
    onGetFeedback,
    isLoading,
    isGettingFeedback,
    disabled
}) {
  const textareaRef = useRef(null);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey && !isLoading && !isGettingFeedback && !disabled && currentMessage.trim()) {
      e.preventDefault();
      onSendMessage();
    }
  };

  const handleSendClick = () => {
      if (!isLoading && !isGettingFeedback && !disabled && currentMessage.trim()) {
          onSendMessage();
      }
  }

  const handleFeedbackClick = () => {
      if (!isLoading && !isGettingFeedback && !disabled) {
          onGetFeedback();
      }
  }

  return (
    // --- ‰ΩøÁî® align-items-center Á¢∫‰øùÂûÇÁõ¥Â±Ö‰∏≠ ---
    <InputGroup className="message-input-group align-items-center">
      {/* Âø´ÈÄüÂõûÈ•ãÊåâÈàï */}
      <Button
        variant="outline-info"
        onClick={handleFeedbackClick}
        disabled={isLoading || isGettingFeedback || disabled}
        title="ËÆì AI ÈáùÂ∞çÊú¨Ê¨°Â∞çË©±Áµ¶Âá∫ÂõûÈ•ã"
        className="feedback-button flex-shrink-0" // Ê∑ªÂä† class
        style={{ height: '40px' }} // Ë®≠ÁΩÆÂõ∫ÂÆöÈ´òÂ∫¶
      >
        {isGettingFeedback ? ( <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" /> ) : ( "ü§î ÊàëË™™ÂæóÂ¶Ç‰ΩïÔºü" )}
      </Button>

      {/* ÊñáÂ≠óËº∏ÂÖ•Ê°Ü */}
      <Form.Control
        ref={textareaRef}
        as="textarea"
        rows={1} // <<<--- ÂàùÂßãË°åÊï∏Ë®≠ÁÇ∫ 1ÔºåËÆìÂÆÉÊ†πÊìöÂÖßÂÆπÂíå min-height/max-height ‰º∏Á∏Æ
        placeholder={disabled ? "Ë´ãÂÖàË®≠ÂÆöÁõÆÊ®ô‰∏¶ÈÅ∏ÊìáËßíËâ≤..." : "Ëº∏ÂÖ•Ë®äÊÅØ (Shift+Enter ÊèõË°å)..."}
        value={currentMessage}
        onChange={(e) => onMessageChange(e.target.value)}
        onKeyDown={handleKeyDown}
        disabled={isLoading || isGettingFeedback || disabled}
        className="message-textarea flex-grow-1 mx-2" // ‰ΩøÁî® flex-grow ‰∏¶Ê∑ªÂä†Â∑¶Âè≥ÈÇäË∑ù
        style={{
            resize: 'none',
            overflowY: 'auto', // Ë∂ÖÂá∫ max-height ÊôÇÊªæÂãï
            minHeight: '40px', // <<<--- Ë®≠ÁΩÆÊúÄÂ∞èÈ´òÂ∫¶ÔºåËàáÊåâÈàïÂ∞çÈΩä
            maxHeight: '100px', // <<<--- ÈôêÂà∂ÊúÄÂ§ßÈ´òÂ∫¶ÔºåÈÅøÂÖçÁÑ°ÈôêÂ¢ûÈï∑
            lineHeight: '1.5' // ÂèØ‰ª•Ë™øÊï¥Ë°åÈ´òÂΩ±ÈüøÂûÇÁõ¥Â±Ö‰∏≠
        }}
        aria-label="Ë®äÊÅØËº∏ÂÖ•Ê°Ü"
      />

      {/* ÁôºÈÄÅÊåâÈàï */}
      <Button
        variant="primary"
        onClick={handleSendClick}
        disabled={isLoading || isGettingFeedback || disabled || !currentMessage.trim()}
        className="send-button flex-shrink-0" // Ê∑ªÂä† class
        style={{ width: '40px', height: '40px' }} // Âõ∫ÂÆöÂ§ßÂ∞è
      >
        {isLoading ? ( <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true"/> ) : ( <i className="bi bi-send"></i> )}
      </Button>
    </InputGroup>
  );
}

export default MessageInput;
