      if (!selectedCharacter || !goal.trim()) return null;
      const goalKey = goal.trim().substring(0, 50).replace(/[^a-zA-Z0-9-_]/g, '_');
      return `${CHAT_HISTORY_PREFIX}${selectedCharacter.id}_${goalKey}`;
