// src/components/CharacterHall/CharacterEvalModal.js (完整版 - 包含所有功能)

import React, { useState, useEffect, useMemo } from 'react';
import { Modal, Button, Alert, ListGroup, Badge, Collapse, Form, InputGroup, Row, Col } from 'react-bootstrap';
import ChartModal from '../FeedbackWall/ChartModal'; // 折線圖 + 指標篩選 Modal
import AverageRadarChart from '../FeedbackWall/AverageRadarChart'; // 雷達圖元件
// 假設 FeedbackChart 在 ChartModal 內部使用
// import FeedbackChart from '../FeedbackWall/FeedbackChart';
import './CharacterHall.css'; // 確保導入 CSS

const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';
// 指標定義
const ALL_METRICS = [
  { key: 'clarity', label: '清晰度' },
  { key: 'empathy', label: '同理心' },
  { key: 'confidence', label: '自信' },
  { key: 'appropriateness', label: '適當性' },
  { key: 'goalAchievement', label: '目標達成' },
];
// 提取分數輔助函數
const getScore = (score) => (score === null || score === undefined ? 0 : score);

function CharacterEvalModal({ show, onHide, character, onDeleteFeedback }) { // 確保 onDeleteFeedback prop 傳遞
  const [originalCharacterFeedback, setOriginalCharacterFeedback] = useState([]);
  const [filteredCharacterFeedback, setFilteredCharacterFeedback] = useState([]);
  const [goalSearchModal, setGoalSearchModal] = useState('');
  const [showEvalFilters, setShowEvalFilters] = useState(false); // 篩選器默認收起
  const [showEvalChartModal, setShowEvalChartModal] = useState(false); // 控制折線圖 Modal
  const [showEvalRadarModal, setShowEvalRadarModal] = useState(false); // 控制雷達圖 Modal

  // 加載和過濾數據
  useEffect(() => {
    let loadedFeedback = [];
    if (show && character) {
      const savedFeedback = localStorage.getItem(FEEDBACK_STORAGE_KEY);
      let allFeedback = [];
      if (savedFeedback) { try { allFeedback = JSON.parse(savedFeedback); if (!Array.isArray(allFeedback)) allFeedback = []; } catch (e) { console.error("EvalModal - Failed to parse feedback history", e); allFeedback = []; } }
      loadedFeedback = allFeedback .filter(fb => fb.characterId === character.id && typeof fb.timestamp === 'number') .sort((a, b) => b.timestamp - a.timestamp); // 按時間降序存儲
      setOriginalCharacterFeedback(loadedFeedback);
      setFilteredCharacterFeedback(loadedFeedback); // 初始顯示所有
      setGoalSearchModal(''); setShowEvalFilters(false); setShowEvalChartModal(false); setShowEvalRadarModal(false); // 重置所有狀態
    } else { setOriginalCharacterFeedback([]); setFilteredCharacterFeedback([]); setGoalSearchModal(''); setShowEvalFilters(false); setShowEvalChartModal(false); setShowEvalRadarModal(false); }
  }, [show, character]);

  // 監聽目標搜尋變化並執行篩選
  useEffect(() => {
    if (!show) return;
    let result = [...originalCharacterFeedback];
    if (goalSearchModal.trim()) { const lowerCaseGoal = goalSearchModal.trim().toLowerCase(); result = result.filter(fb => fb.goal?.toLowerCase().includes(lowerCaseGoal)); }
    setFilteredCharacterFeedback(result);
  }, [originalCharacterFeedback, goalSearchModal, show]);

  // 計算平均分數 (基於篩選後的數據)
  const averageScores = useMemo(() => {
    const scoreSums = { clarity: 0, empathy: 0, confidence: 0, appropriateness: 0, goalAchievement: 0 };
    const scoreCounts = { clarity: 0, empathy: 0, confidence: 0, appropriateness: 0, goalAchievement: 0 };
    filteredCharacterFeedback.forEach(fb => { if (fb.scores) { ALL_METRICS.forEach(metric => { const score = fb.scores[metric.key]; if (score !== null && score !== undefined) { scoreSums[metric.key] += score; scoreCounts[metric.key]++; } }); } });
    const averages = {};
    ALL_METRICS.forEach(metric => { averages[metric.key] = scoreCounts[metric.key] > 0 ? scoreSums[metric.key] / scoreCounts[metric.key] : 0; });
    return averages;
  }, [filteredCharacterFeedback]);

  // 清除 Modal 內的篩選
  const clearModalFilters = () => { setGoalSearchModal(''); };

  return (
    <Modal show={show} onHide={onHide} centered size="lg" dialogClassName="character-eval-modal-dialog">
      <Modal.Header closeButton> <Modal.Title>與 "{character?.name}" 的對話評估</Modal.Title> </Modal.Header>
      <Modal.Body className="d-flex flex-column p-0 character-eval-modal-body">

         {/* --- 可收起的篩選區域 --- */}
         <div className="character-eval-filters bg-light p-3 border-bottom">
           <div className="d-flex justify-content-between align-items-center mb-2"> <h6 className="mb-0 text-muted small">搜尋對話目標</h6> <Button variant="link" size="sm" onClick={() => setShowEvalFilters(!showEvalFilters)} aria-controls="collapse-eval-filters" aria-expanded={showEvalFilters} className="p-0 text-secondary"> {showEvalFilters ? '收起' : '展開'} <i className={`bi bi-chevron-${showEvalFilters ? 'up' : 'down'}`}></i> </Button> </div>
           <Collapse in={showEvalFilters}>
             <div id="collapse-eval-filters">
                <InputGroup size="sm" className="mb-2"> <InputGroup.Text><i className="bi bi-search"></i></InputGroup.Text> <Form.Control type="text" placeholder="搜尋此角色的對話目標..." value={goalSearchModal} onChange={(e) => setGoalSearchModal(e.target.value)} /> </InputGroup>
                 {goalSearchModal && ( <Button variant="outline-secondary" size="sm" onClick={clearModalFilters} className="w-100"> 清除目標搜尋 </Button> )}
             </div>
           </Collapse>
         </div>

        {/* --- 可滾動的內容區域 --- */}
        <div className="character-eval-content flex-grow-1 overflow-auto p-3">
          {originalCharacterFeedback.length > 0 ? (
            <>
              {/* --- 圖表按鈕區域 --- */}
              <div className="text-end mb-3 d-flex justify-content-end">
                   <Button variant="outline-success" size="sm" onClick={() => setShowEvalRadarModal(true)} disabled={filteredCharacterFeedback.length === 0} className="me-2"> <i className="bi bi-bullseye me-1"></i> 查看平均雷達圖 </Button>
                   <Button variant="outline-primary" size="sm" onClick={() => setShowEvalChartModal(true)} disabled={filteredCharacterFeedback.length === 0}> <i className="bi bi-graph-up me-1"></i> 查看趨勢圖表 </Button>
               </div>

              {/* --- 歷史記錄列表 --- */}
              <h5 className="mb-3">詳細回饋記錄 ({filteredCharacterFeedback.length} 筆)</h5>
              {filteredCharacterFeedback.length > 0 ? (
                  <ListGroup variant="flush">
                    {/* 從最新到最舊顯示 */}
                    {filteredCharacterFeedback.map(fb => (
                      <ListGroup.Item key={fb.timestamp} className="px-0 d-flex justify-content-between align-items-start feedback-list-item"> {/* 添加 class */}
                        {/* 左側內容 */}
                        <div className="flex-grow-1 me-2">
                          <div className="d-flex justify-content-between align-items-center mb-2"> <small className="text-muted">{new Date(fb.timestamp).toLocaleString('zh-TW', { dateStyle: 'short', timeStyle: 'short' })}</small> <Badge bg="light" text="dark">目標: {fb.goal || '未指定'}</Badge> </div>
                          <h6>AI 總結:</h6>
                          <p style={{ whiteSpace: 'pre-wrap', fontSize: '0.95em' }}>{fb.summary || '-'}</p>
                          {/* --- 顯示所有評分指標 --- */}
                          {fb.scores && (
                            <>
                              <p className="small mb-1">評分:</p>
                              <ul className="list-inline small mb-0">
                                {ALL_METRICS.map(metric => {
                                  const scoreValue = fb.scores[metric.key];
                                  const displayValue = (scoreValue === null || scoreValue === undefined) ? 'N/A' : getScore(scoreValue);
                                  const badgeBg = displayValue === 'N/A' ? 'light' : (getScore(scoreValue) >= 60 ? "success" : "warning");
                                  return (
                                    <li key={metric.key} className="list-inline-item me-3">
                                      {metric.label}: <Badge pill bg={badgeBg} text={displayValue === 'N/A' ? 'dark' : undefined}>{displayValue}</Badge>
                                    </li>
                                  );
                                })}
                              </ul>
                            </>
                          )}
                        </div>
                        {/* 右側刪除按鈕 */}
                        {onDeleteFeedback && (
                            <Button
                               variant="outline-danger"
                               size="sm"
                               className="delete-feedback-btn p-0 flex-shrink-0" // 使用 class
                               onClick={() => onDeleteFeedback(fb.timestamp)} // 調用傳入的刪除函數
                               title="刪除此筆回饋"
                               // style={{ lineHeight: 1, width: '24px', height: '24px' }} // 樣式移到 CSS
                            >
                               <i className="bi bi-x"></i>
                            </Button>
                        )}
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
              ) : ( <Alert variant="warning">找不到符合目標關鍵字 "{goalSearchModal}" 的記錄。</Alert> )}
            </>
          ) : ( <Alert variant="info">還沒有與此角色的對話評估記錄。</Alert> )}
        </div>
      </Modal.Body>
      <Modal.Footer> <Button variant="secondary" onClick={onHide}> 關閉 </Button> </Modal.Footer>

       {/* --- 折線圖 Modal --- */}
       <ChartModal
          show={showEvalChartModal}
          onHide={() => setShowEvalChartModal(false)}
          feedbackData={filteredCharacterFeedback} // 傳遞篩選後的數據
        />

       {/* --- 雷達圖 Modal --- */}
       <Modal show={showEvalRadarModal} onHide={() => setShowEvalRadarModal(false)} centered>
         <Modal.Header closeButton> <Modal.Title>"{character?.name}" 平均表現雷達圖</Modal.Title> </Modal.Header>
         <Modal.Body>
           {filteredCharacterFeedback.length > 0 ? (
              <AverageRadarChart averageScores={averageScores} /> // 傳遞計算好的平均分
           ) : ( <Alert variant="info">沒有數據可計算平均分數。</Alert> )}
           <p className="text-muted small mt-3"> 注意：此圖表顯示的是當前目標篩選條件下的平均分數。 </p>
         </Modal.Body>
         <Modal.Footer> <Button variant="secondary" onClick={() => setShowEvalRadarModal(false)}> 關閉 </Button> </Modal.Footer>
       </Modal>

    </Modal>
  );
}

export default CharacterEvalModal;
