import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Form, InputGroup, Container, Row, Col, Spinner, Alert, Button, Dropdown } from 'react-bootstrap'; // <<<--- Â∞éÂÖ• Dropdown
import ChatDisplay from './ChatDisplay';
import MessageInput from './MessageInput';
import { sendMessageToOllama, getFeedbackFromOllama } from '../../services/ollamaService'; // ÂºïÂÖ• API ÊúçÂãô
import '../../styles/TrainingRoom.css'; // ÂºïÂÖ•ËÅäÂ§©ÂÆ§Ê®£Âºè

// Êú¨Âú∞ÂÑ≤Â≠òËÅäÂ§©Ë®òÈåÑÁöÑ Key ÂâçÁ∂¥
const CHAT_HISTORY_PREFIX = 'wingchat_history_';
// Êú¨Âú∞ÂÑ≤Â≠òÂõûÈ•ãÁöÑ Key
const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';


// --- Êé•Êî∂Êñ∞ÁöÑ props ---
function TrainingRoom({ characters, selectedCharacter, onSelectCharacter }) {
  const [goal, setGoal] = useState('');
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isGettingFeedback, setIsGettingFeedback] = useState(false);
  const [error, setError] = useState(null);
  const currentGoalRef = useRef(goal);

  // Áî¢ÁîüÁî®ÊñºÂÑ≤Â≠òÁöÑ key
  const getStorageKey = useCallback(() => {
      if (!selectedCharacter || !goal.trim()) return null;
      const goalKey = goal.trim().substring(0, 50).replace(/[^a-zA-Z0-9-_]/g, '_'); // ÂÖÅË®±Â∫ïÁ∑öÂíåÈÄ£Â≠óËôü
      return `${CHAT_HISTORY_PREFIX}${selectedCharacter.id}_${goalKey}`;
  }, [selectedCharacter, goal]);

  // Âä†ËºâËÅäÂ§©Ë®òÈåÑ
  useEffect(() => {
    currentGoalRef.current = goal;
    const storageKey = getStorageKey();
    if (storageKey) {
      const savedHistory = localStorage.getItem(storageKey);
      if (savedHistory) {
        try { setMessages(JSON.parse(savedHistory)); } catch (e) { console.error("Failed to parse chat history", e); setMessages([]); }
      } else { setMessages([]); }
    } else if (selectedCharacter) { setMessages([]); } // Â¶ÇÊûúÊúâËßíËâ≤‰ΩÜÊ≤íÁõÆÊ®ôÊàñkeyÔºå‰πüÊ∏ÖÁ©∫
     setError(null); // ÁõÆÊ®ôÊàñËßíËâ≤ÊîπËÆäÊôÇÊ∏ÖÈô§ÈåØË™§
  }, [selectedCharacter, goal, getStorageKey]); // Áï∂ËßíËâ≤ÊàñÁõÆÊ®ôÊîπËÆäÊôÇËß∏ÁôºÂä†Ëºâ

   // ‰øùÂ≠òËÅäÂ§©Ë®òÈåÑ
   useEffect(() => {
     const storageKey = getStorageKey();
     if (storageKey && messages.length > 0) { localStorage.setItem(storageKey, JSON.stringify(messages)); }
     else if (storageKey && messages.length === 0) { localStorage.removeItem(storageKey); }
   }, [messages, getStorageKey]);

  // Ê∏ÖÈô§Áï∂ÂâçÁõÆÊ®ôÁöÑËÅäÂ§©Ë®òÈåÑ
  const handleClearChat = () => {
      if (window.confirm(`Á¢∫ÂÆöË¶ÅÊ∏ÖÈô§Ëàá ${selectedCharacter?.name} ÈóúÊñºÁõÆÊ®ô„Äå${goal}„ÄçÁöÑÊâÄÊúâËÅäÂ§©Ë®òÈåÑÂóéÔºü`)) {
          setMessages([]);
          const storageKey = getStorageKey();
          if (storageKey) { localStorage.removeItem(storageKey); }
      }
  }

   // ÁôºÈÄÅË®äÊÅØËôïÁêÜ
  const handleSendMessage = async () => {
    if (!currentMessage.trim() || !selectedCharacter) return;
    if (!goal.trim()) { setError("Ë´ãÂÖàË®≠ÂÆöÊú¨Ê¨°ËÅäÂ§©ÁöÑÁõÆÊ®ôÔºÅ"); return; }
    if (isLoading || isGettingFeedback) return;
    const newUserMessage = { id: Date.now(), sender: 'user', text: currentMessage };
    const currentInput = currentMessage;
    setMessages(prevMessages => [...prevMessages, newUserMessage]);
    setCurrentMessage(''); setIsLoading(true); setError(null);
    try {
      const historyForOllama = messages.map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text }));
      const requestHistory = [...historyForOllama, { role: 'user', content: currentInput }];
      // --- ËÆÄÂèñÁí∞Â¢ÉËÆäÊï∏Êàñ‰ΩøÁî®È†êË®≠ÂÄº ---
      const chatModel = process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'huayi-llama3:latest'; // <<<--- ‰ΩøÁî®‰Ω†ÁöÑÊ®°ÂûãÂêç‰ΩúÁÇ∫È†êË®≠
      const aiResponseText = await sendMessageToOllama(goal, requestHistory, selectedCharacter, chatModel); // <<<--- ‰ΩøÁî®ËÆäÊï∏
      const newAiMessage = { id: Date.now() + 1, sender: 'ai', text: aiResponseText };
      setMessages(prevMessages => [...prevMessages, newAiMessage]);
    } catch (err) {
      console.error("Error contacting Ollama:", err);
      const errorMsg = err.message || "Ëàá AI ÈÄöË®äÊôÇÁôºÁîüÈåØË™§„ÄÇ";
      setError(errorMsg);
      const errorAiMessage = { id: Date.now() + 1, sender: 'ai', text: `ü§ñ Êä±Ê≠âÔºåËôïÁêÜÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}` };
      setMessages(prevMessages => [...prevMessages, errorAiMessage]);
    } finally { setIsLoading(false); }
  };

  // --- ÂõûÈ•ãËß£ÊûêÂáΩÊï∏ (‰øùÊåÅ‰∏çËÆä) ---
  const parseFeedback = (feedbackText) => {
      let summary = "ÁÑ°Ê≥ïËß£ÊûêÂõûÈ•ãÊëòË¶Å„ÄÇ"; let scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null };
      let summaryMatch = null; let summaryOnlyMatch = null; let scoresTextMatch = null;
      try {
          summaryMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*?)\s*\[Scores\]/);
          if (summaryMatch && summaryMatch[1]) { summary = summaryMatch[1].trim(); }
          else { summaryOnlyMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*)/); if (summaryOnlyMatch && summaryOnlyMatch[1]) { summary = summaryOnlyMatch[1].trim(); console.warn("Feedback parsing: Only found summary..."); } else { console.warn("Feedback parsing: Could not find [Feedback Summary] section."); summary = feedbackText; } }
          scoresTextMatch = feedbackText.match(/\[Scores\]\s*([\s\S]*)/);
          if (scoresTextMatch && scoresTextMatch[1]) { const scoreLines = scoresTextMatch[1].trim().split('\n'); scoreLines.forEach(line => { const parts = line.split(':'); if (parts.length === 2) { let key = parts[0].trim().toLowerCase().replace(/\s+/g, ''); if (key === 'goalachievement') key = 'goalAchievement'; const value = parts[1].trim(); if (Object.prototype.hasOwnProperty.call(scores, key)) { if (value.toLowerCase() === 'n/a') { scores[key] = null; } else { const score = parseInt(value, 10); if (!isNaN(score)) { scores[key] = Math.max(0, Math.min(100, score)); } else { console.warn(`Invalid score value "${value}" for key "${key}"`); scores[key] = null; } } } else { console.warn(`Unexpected score key "${key}" found.`); } } });
          } else if (!summaryOnlyMatch && summaryMatch === null){ console.warn("Feedback parsing: Could not find [Scores] section..."); }
      } catch (e) { console.error("Error parsing feedback text:", e); summary = feedbackText; scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null }; }
      console.log("Parsed Feedback:", { summary, scores }); return { summary, scores };
  }

  // Áç≤ÂèñÂõûÈ•ãËôïÁêÜ
  const handleGetFeedback = async () => {
      if (!selectedCharacter || !goal.trim() || messages.filter(m => m.sender !== 'system').length === 0) { alert("Ë´ãÂÖàË®≠ÂÆöÁõÆÊ®ô„ÄÅÈÅ∏ÊìáËßíËâ≤‰∏¶ÈÄ≤Ë°åËá≥Â∞ë‰∏ÄËº™Â∞çË©±ÔºåÊâçËÉΩË´ãÊ±ÇÂõûÈ•ã„ÄÇ"); return; }
      if (isLoading || isGettingFeedback) return;
      setIsGettingFeedback(true); setError(null); const feedbackRequestMessage = { id: Date.now(), sender: 'system', text: '‚è≥ Ê≠£Âú®Âêë AI Ë´ãÊ±ÇÂ∞çË©±ÂõûÈ•ã...' }; setMessages(prevMessages => [...prevMessages, feedbackRequestMessage]);
      try {
          const historyForFeedback = messages.filter(msg => msg.sender !== 'system').map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text }));
          // --- ËÆÄÂèñÁí∞Â¢ÉËÆäÊï∏Êàñ‰ΩøÁî®È†êË®≠ÂÄº ---
          const feedbackModel = process.env.REACT_APP_OLLAMA_FEEDBACK_MODEL || process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'huayi-llama3:latest'; // <<<--- ÂÑ™ÂÖàÁî®ÂõûÈ•ãÊ®°ÂûãÔºåÂê¶ÂâáÁî®ËÅäÂ§©Ê®°ÂûãÔºåÂÜçÂê¶ÂâáÁî®È†êË®≠
          const feedbackRawText = await getFeedbackFromOllama(goal, historyForFeedback, selectedCharacter, feedbackModel); // <<<--- ‰ΩøÁî®ËÆäÊï∏
          const { summary, scores } = parseFeedback(feedbackRawText);
          const feedbackMessage = { id: Date.now() + 1, sender: 'ai', text: `üí° **Êú¨Ê¨°Â∞çË©±ÂõûÈ•ã (${goal})**\n\n**ÊëòË¶Å:**\n${summary}\n\n**Ë©ïÂàÜ (0-100):**\n- Ê∏ÖÊô∞Â∫¶: ${scores.clarity ?? 'N/A'}\n- ÂêåÁêÜÂøÉ: ${scores.empathy ?? 'N/A'}\n- Ëá™‰ø°: ${scores.confidence ?? 'N/A'}\n- ÈÅ©Áï∂ÊÄß: ${scores.appropriateness ?? 'N/A'}\n- ÁõÆÊ®ôÈÅîÊàê: ${scores.goalAchievement ?? 'N/A'}` };
          const newFeedbackEntry = { timestamp: Date.now(), goal: goal, characterId: selectedCharacter.id, characterName: selectedCharacter.name, scores: scores, summary: summary, rawFeedback: feedbackRawText };
          const existingFeedback = JSON.parse(localStorage.getItem(FEEDBACK_STORAGE_KEY) || '[]'); localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify([...existingFeedback, newFeedbackEntry]));
          setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), feedbackMessage]);
      } catch (err) {
          console.error("Error getting or parsing feedback from Ollama:", err); const errorMsg = err.message || "ÁÑ°Ê≥ïÁç≤ÂèñÊàñËôïÁêÜ AI ÂõûÈ•ã„ÄÇ"; setError(errorMsg); const errorFeedbackMessage = { id: Date.now() + 1, sender: 'ai', text: `ü§ñ Êä±Ê≠âÔºåÁç≤ÂèñÂõûÈ•ãÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}` }; setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), errorFeedbackMessage]);
      } finally { setIsGettingFeedback(false); }
  };

  return (
    <Container fluid className="training-room-container vh-100">
      {/* --- ÁõÆÊ®ôËº∏ÂÖ•ËàáËßíËâ≤Ë≥áË®ä --- */}
      <Row className="goal-input-row bg-light p-2 mb-2 sticky-top shadow-sm align-items-center"> {/* Ê∑ªÂä† align-items-center */}
        {/* ÁõÆÊ®ôËº∏ÂÖ•Ê°Ü */}
        <Col md={6}> {/* Á®çÂæÆË™øÊï¥ÂØ¨Â∫¶ */}
          <InputGroup>
            <InputGroup.Text>üéØ</InputGroup.Text>
            <Form.Control
              type="text"
              placeholder={selectedCharacter ? "Ëº∏ÂÖ•Êú¨Ê¨°Á§æ‰∫§Ë®ìÁ∑¥ÁõÆÊ®ô..." : "Ë´ãÂÖàÈÅ∏ÊìáËßíËâ≤..."}
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              disabled={!selectedCharacter || isLoading || isGettingFeedback}
              aria-label="ËÅäÂ§©ÁõÆÊ®ô"
            />
          </InputGroup>
        </Col>

        {/* ËßíËâ≤ÈÅ∏ÊìáÂíåÊ∏ÖÈô§ÊåâÈàï */}
        <Col md={6} className="d-flex align-items-center justify-content-md-end mt-2 mt-md-0"> {/* Á®çÂæÆË™øÊï¥ÂØ¨Â∫¶ */}
          {characters && characters.length > 0 ? ( // Á¢∫‰øùÊúâËßíËâ≤ÊâçÈ°ØÁ§∫ Dropdown
            <Dropdown className="me-2" style={{ minWidth: '150px' }}> {/* Ê∑ªÂä†ÊúÄÂ∞èÂØ¨Â∫¶ */}
              <Dropdown.Toggle variant="outline-secondary" size="sm" id="dropdown-character-select" disabled={isLoading || isGettingFeedback}>
                Ëàá: {selectedCharacter ? selectedCharacter.name : 'ÈÅ∏ÊìáËßíËâ≤'}
              </Dropdown.Toggle>
              <Dropdown.Menu style={{maxHeight: '300px', overflowY: 'auto'}}> {/* Ê∑ªÂä†ÊúÄÂ§ßÈ´òÂ∫¶ÂíåÊªæÂãï */}
                {characters.map(character => (
                  <Dropdown.Item
                    key={character.id}
                    active={selectedCharacter?.id === character.id}
                    onClick={() => onSelectCharacter(character)} // <<<--- Ë™øÁî®Âæû App ÂÇ≥‰æÜÁöÑÂáΩÊï∏
                  >
                    {character.name}
                  </Dropdown.Item>
                ))}
              </Dropdown.Menu>
            </Dropdown>
           ) : (
             <span className="text-danger me-2 small">Ë´ãÂÖàËá≥ËßíËâ≤È§®Êñ∞Â¢ûËßíËâ≤</span>
           )}

          {/* Ê∏ÖÈô§Ë®òÈåÑÊåâÈàï (Âè™ÊúâÈÅ∏Êìá‰∫ÜËßíËâ≤ÂíåÁõÆÊ®ô‰∏îÊúâË®äÊÅØÊôÇÊâçÈ°ØÁ§∫) */}
          {selectedCharacter && goal.trim() && messages.length > 0 && (
            <Button
              variant="outline-secondary"
              size="sm"
              onClick={handleClearChat}
              disabled={isLoading || isGettingFeedback}
              title={`Ê∏ÖÈô§ÈóúÊñºÁõÆÊ®ô "${goal}" ÁöÑËÅäÂ§©Ë®òÈåÑ`}
            >
              <i className="bi bi-trash3 me-1"></i> Ê∏ÖÈô§Ë®òÈåÑ
            </Button>
          )}
        </Col>

        {/* ÈåØË™§ÊèêÁ§∫ */}
        {error && (
            <Col xs={12} className="mt-2">
                <Alert variant="danger" onClose={() => setError(null)} dismissible className="py-1 px-2 small mb-0">
                    {error}
                </Alert>
            </Col>
        )}
      </Row>

      {/* --- ËÅäÂ§©È°ØÁ§∫ÂçÄÂüü --- */}
      <Row className="chat-display-row">
        <Col>
          <ChatDisplay messages={messages} isLoading={isLoading} />
        </Col>
      </Row>

      {/* --- Ë®äÊÅØËº∏ÂÖ•ÂçÄÂüü --- */}
      <Row className="message-input-row sticky-bottom bg-light p-3">
        <Col>
          <MessageInput
            currentMessage={currentMessage}
            onMessageChange={setCurrentMessage}
            onSendMessage={handleSendMessage}
            onGetFeedback={handleGetFeedback}
            isLoading={isLoading}
            isGettingFeedback={isGettingFeedback}
            disabled={!selectedCharacter || !goal.trim()} // <<<--- Á¢∫‰øùÈÅ∏‰∏≠ËßíËâ≤ÂíåÁõÆÊ®ôÂæåÊâçËÉΩËº∏ÂÖ•
          />
        </Col>
      </Row>
    </Container>
  );
}

