// src/components/FeedbackWall/FeedbackChart.js (確認是折線圖版本)

import React from 'react';
import { Line } from 'react-chartjs-2'; // <<<--- 確認是 Line
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, // <<<--- 確認導入 LineElement, PointElement
  Title, Tooltip, Legend, Filler
} from 'chart.js';

ChartJS.register( CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler ); // <<<--- 確認註冊

function FeedbackChart({ labels, datasets }) {
  const data = {
    labels: labels,
    datasets: datasets.map(ds => ({ ...ds, fill: false, tension: 0.1, pointBackgroundColor: ds.borderColor, pointBorderColor: '#fff', pointHoverBackgroundColor: '#fff', pointHoverBorderColor: ds.borderColor })),
  };
  const options = { responsive: true, maintainAspectRatio: false, plugins: { legend: { position: 'top', }, title: { display: true, text: '社交技能評分趨勢 (0-100分)', }, tooltip: { mode: 'index', intersect: false, } }, scales: { y: { beginAtZero: true, max: 100, title: { display: true, text: '分數' } }, x: { title: { display: true, text: '練習時間' }, ticks: { autoSkip: true, maxRotation: 45, minRotation: 0 } } }, interaction: { mode: 'index', intersect: false, }, };

  return (
    <div style={{ position: 'relative', height: '300px', width: '100%' }}>
       <Line options={options} data={data} /> {/* <<<--- 確認使用 Line */}
    </div>
   );
}

export default FeedbackChart;
