// src/components/CharacterHall/AddCharacterModal.js (在 Modal 內顯示錯誤)

import React, { useState, useEffect } from 'react';
// --- 導入 Alert ---
import { Modal, Button, Form, Alert } from 'react-bootstrap';

// --- 修改 prop 名稱，接收 onAddCharacterRequest ---
function AddCharacterModal({ show, onHide, onAddCharacterRequest }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [validated, setValidated] = useState(false);
  const [errorMsg, setErrorMsg] = useState(null); // <<<--- 新增錯誤訊息狀態

  // 當 Modal 顯示時，清空狀態
  useEffect(() => {
    if (show) {
      setName('');
      setDescription('');
      setValidated(false);
      setErrorMsg(null);
    }
  }, [show]);

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    event.preventDefault();
    event.stopPropagation();
    setErrorMsg(null); // 清除舊錯誤

    if (form.checkValidity() === false) {
      setValidated(true); // Bootstrap 的空值驗證
    } else {
      // --- 調用 CharacterHall 傳來的檢查函數 ---
      const success = onAddCharacterRequest({ name, description }); // <<<--- 調用新的 prop 函數

      if (success) {
        // --- 新增成功，執行清理和關閉 ---
        // 清理已移到 useEffect
        onHide(); // 關閉 Modal
      } else {
        // --- 新增失敗 (名稱重複)，在 Modal 內部顯示錯誤訊息 ---
        setErrorMsg(`角色名稱 "${name.trim()}" 已經存在，請修改。`);
        setValidated(false); // 清除 Bootstrap 的驗證樣式
      }
    }
  };

  // 關閉按鈕或背景點擊時的處理
  const handleClose = () => {
    onHide();
  }

  return (
    // 添加 backdrop="static" 防止點背景關閉，讓用戶必須處理錯誤
    <Modal show={show} onHide={handleClose} centered backdrop="static">
      <Modal.Header closeButton>
        <Modal.Title>新增 AI 角色</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* --- 在表單頂部顯示錯誤訊息 --- */}
        {errorMsg && <Alert variant="danger">{errorMsg}</Alert>}

        <Form noValidate validated={validated} onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="characterName">
            <Form.Label>角色名稱 <span className="text-danger">*</span></Form.Label>
            <Form.Control
              type="text"
              placeholder="例如：面試官、健談的鄰居"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              maxLength={50}
              isInvalid={!!errorMsg || (validated && !name.trim())} // <<<--- 錯誤或空值時標紅
            />
            {/* Bootstrap 的驗證回饋 + 自訂錯誤 */}
            <Form.Control.Feedback type="invalid">
              {errorMsg ? errorMsg : '請輸入角色名稱 (最多 50 字)。'}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className="mb-3" controlId="characterDescription">
            <Form.Label>角色描述 (AI 將依此扮演) <span className="text-danger">*</span></Form.Label>
            <Form.Control
              as="textarea"
              rows={4}
              placeholder="請詳細描述角色的性格、背景、語氣和行為方式..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              maxLength={500}
            />
             <Form.Text className="text-muted">
               描述越詳細，AI 扮演得越像。 (最多 500 字)
             </Form.Text>
            <Form.Control.Feedback type="invalid">
              請輸入角色描述。
            </Form.Control.Feedback>
          </Form.Group>
          <div className="d-flex justify-content-end">
             <Button variant="secondary" onClick={handleClose} className="me-2">
               取消
             </Button>
             <Button variant="primary" type="submit">
               儲存角色
             </Button>
          </div>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default AddCharacterModal;
