// src/components/TrainingRoom/TrainingRoom.js (‰øÆÊîπÁâà)

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Form, InputGroup, Container, Row, Col, Spinner, Alert, Button } from 'react-bootstrap';
import ChatDisplay from './ChatDisplay';
import MessageInput from './MessageInput';
import { sendMessageToOllama, getFeedbackFromOllama } from '../../services/ollamaService';
import '../../styles/TrainingRoom.css';

const CHAT_HISTORY_PREFIX = 'wingchat_history_';
const FEEDBACK_STORAGE_KEY = 'wingchat_feedback';

// --- Êé•Êî∂Êñ∞ÁöÑ props ---
function TrainingRoom({ characters, selectedCharacter, onCharacterChange }) {
  const [goal, setGoal] = useState('');
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isGettingFeedback, setIsGettingFeedback] = useState(false);
  const [error, setError] = useState(null);
  const currentGoalRef = useRef(goal);

  const getStorageKey = useCallback(() => {
      if (!selectedCharacter || !goal.trim()) return null;
      const goalKey = goal.trim().substring(0, 50).replace(/[^a-zA-Z0-9-_]/g, '_');
      // *** ‰ΩøÁî® selectedCharacter.id ***
      return `${CHAT_HISTORY_PREFIX}${selectedCharacter.id}_${goalKey}`;
  }, [selectedCharacter, goal]); // ‰æùË≥¥ selectedCharacter

  useEffect(() => {
    currentGoalRef.current = goal;
    const storageKey = getStorageKey();
    if (storageKey) {
      const savedHistory = localStorage.getItem(storageKey);
      if (savedHistory) {
        try { setMessages(JSON.parse(savedHistory)); }
        catch (e) { console.error("Failed to parse chat history", e); setMessages([]); }
      } else { setMessages([]); }
    } else if (selectedCharacter) { setMessages([]); } // Â¶ÇÊûúÂè™ÊúâËßíËâ≤Ôºå‰πüÊ∏ÖÁ©∫
     setError(null);
  // *** ‰æùË≥¥ selectedCharacter ***
  }, [selectedCharacter, goal, getStorageKey]);

   useEffect(() => {
     const storageKey = getStorageKey();
     if (storageKey && messages.length > 0) { localStorage.setItem(storageKey, JSON.stringify(messages)); }
     else if (storageKey && messages.length === 0) { localStorage.removeItem(storageKey); }
   }, [messages, getStorageKey]); // getStorageKey ‰æùË≥¥ selectedCharacter, goal

  const handleClearChat = () => {
      if (window.confirm(`Á¢∫ÂÆöË¶ÅÊ∏ÖÈô§Ëàá ${selectedCharacter?.name} ÈóúÊñºÁõÆÊ®ô„Äå${goal}„ÄçÁöÑÊâÄÊúâËÅäÂ§©Ë®òÈåÑÂóéÔºü`)) {
          setMessages([]);
          const storageKey = getStorageKey();
          if (storageKey) { localStorage.removeItem(storageKey); }
      }
  }

  // --- handleCharacterSelect: ËôïÁêÜ‰∏ãÊãâÈÅ∏ÂñÆËÆäÊõ¥ ---
  const handleCharacterSelect = (event) => {
      const characterId = event.target.value;
      if (characterId !== selectedCharacter?.id) {
          // Ê∏ÖÁ©∫Áï∂ÂâçËÅäÂ§©Ë®òÈåÑÂíåÁõÆÊ®ôÊòØ‰∏ÄÂÄãÂ•ΩÁöÑÂÅöÊ≥ïÔºåÂõ†ÁÇ∫‰∏ä‰∏ãÊñáËÆä‰∫Ü
          setMessages([]);
          setGoal(''); // Ê∏ÖÁ©∫ÁõÆÊ®ôËÆìÁî®Êà∂ÈáçÊñ∞Ë®≠ÂÆö
          setError(null);
          // Ë™øÁî® App.js ÂÇ≥‰æÜÁöÑÂáΩÊï∏Êõ¥Êñ∞ÈÅ∏‰∏≠ÁöÑËßíËâ≤
          onCharacterChange(characterId);
      }
  };
  // --- ÁµêÊùüÊñ∞Â¢û ---


  const handleSendMessage = async () => {
    // ... (ÂÖßÈÉ®ÈÇèËºØ‰∏çËÆäÔºå‰ΩøÁî® selectedCharacter) ...
    // *** Á¢∫Ë™çÊ®°ÂûãÂêçÁ®±ÊòØ‰Ω†ÊÉ≥Áî®ÁöÑ ***
    const modelToUse = "huayi-llama3:latest"; // ÊàñËÄÖÂæû .env ËÆÄÂèñ
    try {
        const historyForOllama = messages.map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text }));
        const requestHistory = [...historyForOllama, { role: 'user', content: currentInput }];
        const aiResponseText = await sendMessageToOllama(goal, requestHistory, selectedCharacter, modelToUse); // ‰ΩøÁî® modelToUse
        // ... (ËôïÁêÜÂõûÊáâ)
    } catch (err) {
        // ... (ÈåØË™§ËôïÁêÜ)
    } finally { setIsLoading(false); }
  };

  const parseFeedback = (feedbackText) => {
    // ... (Ëß£ÊûêÈÇèËºØ‰∏çËÆä) ...
    let summary = "ÁÑ°Ê≥ïËß£ÊûêÂõûÈ•ãÊëòË¶Å„ÄÇ";
    let scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null };
    let summaryMatch = null; let summaryOnlyMatch = null; let scoresTextMatch = null;
    try {
        summaryMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*?)\s*\[Scores\]/);
        if (summaryMatch && summaryMatch[1]) { summary = summaryMatch[1].trim(); }
        else {
            summaryOnlyMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*)/);
            if (summaryOnlyMatch && summaryOnlyMatch[1]) { summary = summaryOnlyMatch[1].trim(); console.warn("Feedback parsing: Only found summary."); }
            else { console.warn("Feedback parsing: Could not find [Feedback Summary]."); summary = feedbackText; }
        }
        scoresTextMatch = feedbackText.match(/\[Scores\]\s*([\s\S]*)/);
        if (scoresTextMatch && scoresTextMatch[1]) {
            const scoreLines = scoresTextMatch[1].trim().split('\n');
            scoreLines.forEach(line => {
                const parts = line.split(':');
                if (parts.length === 2) {
                    let key = parts[0].trim().toLowerCase().replace(/\s+/g, '');
                    if (key === 'goalachievement') key = 'goalAchievement';
                    const value = parts[1].trim();
                    if (Object.prototype.hasOwnProperty.call(scores, key)) {
                         if (value.toLowerCase() === 'n/a') { scores[key] = null; }
                         else { const score = parseInt(value, 10); if (!isNaN(score)) { scores[key] = Math.max(0, Math.min(100, score)); } else { console.warn(`Invalid score value: ${value}`); scores[key] = null; } }
                    } else { console.warn(`Unexpected score key: ${key}`); }
                }
            });
        } else if (!summaryOnlyMatch && summaryMatch === null){ console.warn("Feedback parsing: Could not find [Scores]."); }
    } catch (e) { console.error("Error parsing feedback text:", e); summary = feedbackText; scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null }; }
    console.log("Parsed Feedback:", { summary, scores });
    return { summary, scores };
  }

  const handleGetFeedback = async () => {
    // ... (ÂÖßÈÉ®ÈÇèËºØ‰∏çËÆäÔºå‰ΩøÁî® selectedCharacter) ...
    // *** Á¢∫Ë™çÊ®°ÂûãÂêçÁ®±ÊòØ‰Ω†ÊÉ≥Áî®ÁöÑ ***
     const modelToUse = "huayi-llama3:latest"; // ÊàñËÄÖÂæû .env ËÆÄÂèñ
     try {
