         const feedbackRawText = await getFeedbackFromOllama(goal, historyForFeedback, selectedCharacter, modelToUse); // ‰ΩøÁî® modelToUse
         // ... (Ëß£Êûê„ÄÅÂÑ≤Â≠ò„ÄÅÈ°ØÁ§∫ÂõûÈ•ã)
          const { summary, scores } = parseFeedback(feedbackRawText);
          const feedbackMessage = { id: Date.now() + 1, sender: 'ai', text: `üí° **Êú¨Ê¨°Â∞çË©±ÂõûÈ•ã (${goal})**\n\n**ÊëòË¶Å:**\n${summary}\n\n**Ë©ïÂàÜ (0-100):**\n- Ê∏ÖÊô∞Â∫¶: ${scores.clarity ?? 'N/A'}\n- ÂêåÁêÜÂøÉ: ${scores.empathy ?? 'N/A'}\n- Ëá™‰ø°: ${scores.confidence ?? 'N/A'}\n- ÈÅ©Áï∂ÊÄß: ${scores.appropriateness ?? 'N/A'}\n- ÁõÆÊ®ôÈÅîÊàê: ${scores.goalAchievement ?? 'N/A'}` };
          const newFeedbackEntry = { timestamp: Date.now(), goal: goal, characterId: selectedCharacter.id, characterName: selectedCharacter.name, scores: scores, summary: summary, rawFeedback: feedbackRawText };
          const existingFeedback = JSON.parse(localStorage.getItem(FEEDBACK_STORAGE_KEY) || '[]');
          localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify([...existingFeedback, newFeedbackEntry]));
          setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), feedbackMessage]);
     } catch (err) {
         // ... (ÈåØË™§ËôïÁêÜ)
         console.error("Error getting or parsing feedback from Ollama:", err);
         const errorMsg = err.message || "ÁÑ°Ê≥ïÁç≤ÂèñÊàñËôïÁêÜ AI ÂõûÈ•ã„ÄÇ";
         setError(errorMsg);
         const errorFeedbackMessage = { id: Date.now() + 1, sender: 'ai', text: `ü§ñ Êä±Ê≠âÔºåÁç≤ÂèñÂõûÈ•ãÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}` };
          setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), errorFeedbackMessage]);
     } finally { setIsGettingFeedback(false); }
  };

  return (
    <Container fluid className="training-room-container vh-100">
      {/* --- ÁõÆÊ®ôËº∏ÂÖ•ËàáËßíËâ≤Ë≥áË®ä (‰øÆÊîπ‰ΩàÂ±Ä) --- */}
      <Row className="goal-input-row bg-light p-2 mb-2 sticky-top shadow-sm align-items-center"> {/* ‰ΩøÁî® align-items-center ÂûÇÁõ¥Â±Ö‰∏≠ */}
        {/* ÁõÆÊ®ôËº∏ÂÖ•Ê°Ü */}
        <Col md={6} className="mb-2 mb-md-0"> {/* Ë™øÊï¥Ê¨ÑÂØ¨ */}
          <InputGroup>
            <InputGroup.Text>üéØ</InputGroup.Text>
            <Form.Control
              type="text"
              placeholder={selectedCharacter ? "Ëº∏ÂÖ•Êú¨Ê¨°Á§æ‰∫§Ë®ìÁ∑¥ÁõÆÊ®ô..." : "Ë´ãÂÖàÈÅ∏ÊìáËßíËâ≤..."}
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              disabled={!selectedCharacter || isLoading || isGettingFeedback}
              aria-label="ËÅäÂ§©ÁõÆÊ®ô"
            />
          </InputGroup>
        </Col>

        {/* ËßíËâ≤ÈÅ∏ÊìáÂíåÊ∏ÖÈô§ÊåâÈàï */}
        <Col md={6} className="d-flex justify-content-md-end align-items-center"> {/* Ë™øÊï¥Ê¨ÑÂØ¨‰∏¶Èù†Âè≥ */}
          {characters && characters.length > 0 ? (
            <div className="d-flex align-items-center w-100"> {/* ËÆìÂÖßÈÉ®ÂÖÉÁ¥†‰ΩàÂ±Ä */}
              <Form.Select
                aria-label="ÈÅ∏ÊìáËßíËâ≤"
                value={selectedCharacter?.id || ''} // Á¢∫‰øùÊúâÂÄºÔºåÂê¶ÂâáÁµ¶Á©∫Â≠ó‰∏≤
                onChange={handleCharacterSelect}
                disabled={isLoading || isGettingFeedback}
                size="sm" // ËÆì‰∏ãÊãâÈÅ∏ÂñÆÂ∞è‰∏ÄÈªû
                className="me-2 flex-grow-1" // ‰ΩîÊìöÁ©∫Èñì‰∏¶ËàáÊåâÈàïÈñìÈöî
                style={{ maxWidth: '250px' }} // ÈôêÂà∂ÊúÄÂ§ßÂØ¨Â∫¶
              >
                {/* ÂèØ‰ª•Âä†‰∏ÄÂÄãÈ†êË®≠ÊèêÁ§∫ÈÅ∏È†Ö */}
                {/* <option value="" disabled={selectedCharacter !== null}>-- ÈÅ∏ÊìáÂ∞çË©±ËßíËâ≤ --</option> */}
                {characters.map(char => (
