  const [error, setError] = useState(null);
  const currentGoalRef = useRef(goal);

  const getStorageKey = useCallback(() => {
      if (!selectedCharacter || !goal.trim()) return null;
      const goalKey = goal.trim().substring(0, 50).replace(/[^a-zA-Z0-9-_]/g, '_');
      return `${CHAT_HISTORY_PREFIX}${selectedCharacter.id}_${goalKey}`;
  }, [selectedCharacter, goal]);

  useEffect(() => {
    currentGoalRef.current = goal;
    const storageKey = getStorageKey();
    if (storageKey) {
      const savedHistory = localStorage.getItem(storageKey);
      if (savedHistory) { try { setMessages(JSON.parse(savedHistory)); } catch (e) { console.error("Parsing chat history failed", e); setMessages([]); } }
      else { setMessages([]); }
    } else if (selectedCharacter) { setMessages([]); }
    setError(null);
  }, [selectedCharacter, goal, getStorageKey]);

  useEffect(() => {
    const storageKey = getStorageKey();
    if (storageKey && messages.length > 0) { localStorage.setItem(storageKey, JSON.stringify(messages)); }
    else if (storageKey && messages.length === 0) { localStorage.removeItem(storageKey); }
  }, [messages, getStorageKey]);

  const handleClearChat = () => { /* ... (保持不變) ... */ if (window.confirm(`確定要清除與 ${selectedCharacter?.name} 關於目標「${goal}」的所有聊天記錄嗎？`)) { setMessages([]); const storageKey = getStorageKey(); if (storageKey) { localStorage.removeItem(storageKey); } } };
  const handleSendMessage = async () => { /* ... (保持不變，確保使用 process.env 讀取模型) ... */ if (!currentMessage.trim() || !selectedCharacter) return; if (!goal.trim()) { setError("請先設定本次聊天的目標！"); return; } if (isLoading || isGettingFeedback) return; const newUserMessage = { id: Date.now(), sender: 'user', text: currentMessage }; const currentInput = currentMessage; setMessages(prevMessages => [...prevMessages, newUserMessage]); setCurrentMessage(''); setIsLoading(true); setError(null); try { const historyForOllama = messages.map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text })); const requestHistory = [...historyForOllama, { role: 'user', content: currentInput }]; const chatModel = process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'huayi-llama3:latest'; const aiResponseText = await sendMessageToOllama(goal, requestHistory, selectedCharacter, chatModel); const newAiMessage = { id: Date.now() + 1, sender: 'ai', text: aiResponseText }; setMessages(prevMessages => [...prevMessages, newAiMessage]); } catch (err) { console.error("Error contacting Ollama:", err); const errorMsg = err.message || "與 AI 通訊時發生錯誤。"; setError(errorMsg); const errorAiMessage = { id: Date.now() + 1, sender: 'ai', text: `🤖 抱歉，處理時遇到問題：${errorMsg}` }; setMessages(prevMessages => [...prevMessages, errorAiMessage]); } finally { setIsLoading(false); } };
  const parseFeedback = (feedbackText) => { /* ... (保持不變) ... */ let summary = "無法解析回饋摘要。"; let scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null }; let summaryMatch = null; let summaryOnlyMatch = null; let scoresTextMatch = null; try { summaryMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*?)\s*\[Scores\]/); if (summaryMatch && summaryMatch[1]) { summary = summaryMatch[1].trim(); } else { summaryOnlyMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*)/); if (summaryOnlyMatch && summaryOnlyMatch[1]) { summary = summaryOnlyMatch[1].trim(); console.warn("Only found summary..."); } else { console.warn("Could not find [Feedback Summary] section."); summary = feedbackText; } } scoresTextMatch = feedbackText.match(/\[Scores\]\s*([\s\S]*)/); if (scoresTextMatch && scoresTextMatch[1]) { const scoreLines = scoresTextMatch[1].trim().split('\n'); scoreLines.forEach(line => { const parts = line.split(':'); if (parts.length === 2) { let key = parts[0].trim().toLowerCase().replace(/\s+/g, ''); if (key === 'goalachievement') key = 'goalAchievement'; const value = parts[1].trim(); if (Object.prototype.hasOwnProperty.call(scores, key)) { if (value.toLowerCase() === 'n/a') { scores[key] = null; } else { const score = parseInt(value, 10); if (!isNaN(score)) { scores[key] = Math.max(0, Math.min(100, score)); } else { console.warn(`Invalid score value "${value}" for key "${key}"`); scores[key] = null; } } } else { console.warn(`Unexpected score key "${key}" found.`); } } }); } else if (!summaryOnlyMatch && summaryMatch === null){ console.warn("Could not find [Scores] section..."); } } catch (e) { console.error("Error parsing feedback text:", e); summary = feedbackText; scores = { clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null }; } console.log("Parsed Feedback:", { summary, scores }); return { summary, scores }; };
  const handleGetFeedback = async () => { /* ... (保持不變，確保使用 process.env 讀取模型) ... */ if (!selectedCharacter || !goal.trim() || messages.filter(m => m.sender !== 'system').length === 0) { alert("請先設定目標、選擇角色並進行至少一輪對話，才能請求回饋。"); return; } if (isLoading || isGettingFeedback) return; setIsGettingFeedback(true); setError(null); const feedbackRequestMessage = { id: Date.now(), sender: 'system', text: '⏳ 正在向 AI 請求對話回饋...' }; setMessages(prevMessages => [...prevMessages, feedbackRequestMessage]); try { const historyForFeedback = messages.filter(msg => msg.sender !== 'system').map(msg => ({ role: msg.sender === 'user' ? 'user' : 'assistant', content: msg.text })); const feedbackModel = process.env.REACT_APP_OLLAMA_FEEDBACK_MODEL || process.env.REACT_APP_OLLAMA_CHAT_MODEL || 'huayi-llama3:latest'; const feedbackRawText = await getFeedbackFromOllama(goal, historyForFeedback, selectedCharacter, feedbackModel); const { summary, scores } = parseFeedback(feedbackRawText); const feedbackMessage = { id: Date.now() + 1, sender: 'ai', isFeedback: true, text: `💡 **本次對話回饋 (${goal})**\n\n**摘要:**\n${summary}\n\n**評分 (0-100):**\n- 清晰度: ${scores.clarity ?? 'N/A'}\n- 同理心: ${scores.empathy ?? 'N/A'}\n- 自信: ${scores.confidence ?? 'N/A'}\n- 適當性: ${scores.appropriateness ?? 'N/A'}\n- 目標達成: ${scores.goalAchievement ?? 'N/A'}` }; const newFeedbackEntry = { timestamp: Date.now(), goal: goal, characterId: selectedCharacter.id, characterName: selectedCharacter.name, scores: scores, summary: summary, rawFeedback: feedbackRawText }; const existingFeedback = JSON.parse(localStorage.getItem(FEEDBACK_STORAGE_KEY) || '[]'); localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify([...existingFeedback, newFeedbackEntry])); setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), feedbackMessage]); } catch (err) { console.error("Error getting or parsing feedback from Ollama:", err); const errorMsg = err.message || "無法獲取或處理 AI 回饋。"; setError(errorMsg); const errorFeedbackMessage = { id: Date.now() + 1, sender: 'ai', text: `🤖 抱歉，獲取回饋時遇到問題：${errorMsg}` }; setMessages(prevMessages => [...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id), errorFeedbackMessage]); } finally { setIsGettingFeedback(false); } };

  return (
    // 確保使用了 training-room-container class
    <Container fluid className="training-room-container p-0">
      {/* --- 目標輸入與角色選擇 --- */}
      {/* 使用 goal-input-row class */}
      <Row className="goal-input-row m-0 align-items-center">
        {/* 目標輸入 */}
        <Col md={7} className="mb-2 mb-md-0"> {/* 調整寬度並添加響應式邊距 */}
          <InputGroup>
            <InputGroup.Text>🎯</InputGroup.Text>
            <Form.Control
              type="text"
              placeholder="輸入本次社交訓練目標..." // 移除 disabled 判斷，如果沒角色下面會提示
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              disabled={isLoading || isGettingFeedback} // 主要是防止請求過程中修改
              aria-label="聊天目標"
            />
          </InputGroup>
        </Col>

        {/* 角色選擇和清除按鈕 */}
        <Col md={5} className="d-flex align-items-center justify-content-md-end">
          {/* --- 添加角色選擇 Dropdown --- */}
          {characters && characters.length > 0 ? (
            <Dropdown className="me-3" id="character-selector-dropdown"> {/* 增加右邊距 */}
              <Dropdown.Toggle
                 variant="outline-secondary"
                 size="sm"
                 id="dropdown-character-select-button" // 添加唯一 ID
                 disabled={isLoading || isGettingFeedback}
                 style={{minWidth: '120px', textAlign: 'left'}} // 最小寬度和文本對齊
              >
                與: {selectedCharacter ? <strong className="text-primary">{selectedCharacter.name}</strong> : '選擇角色'}
              </Dropdown.Toggle>
              <Dropdown.Menu style={{maxHeight: '300px', overflowY: 'auto'}}>
                {characters.map(character => (
                  <Dropdown.Item
                    key={character.id}
                    active={selectedCharacter?.id === character.id}
                    onClick={() => onSelectCharacter(character)} // <<<--- 調用 prop 函數
                    disabled={selectedCharacter?.id === character.id} // 禁用當前已選項
                  >
                    {character.name}
                  </Dropdown.Item>
                ))}
                 {/* 可以加一個分隔線和回到角色館的選項 */}
                 <Dropdown.Divider />
                 <Dropdown.Item onClick={() => alert('請點擊上方導覽列的「角色館」')}>
                   <small className="text-muted">管理角色...</small>
                 </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
           ) : (
             <span className="text-danger me-3 small">請先至角色館新增角色</span> // 如果完全沒角色
           )}
          {/* --- 結束添加 Dropdown --- */}

          {/* 清除記錄按鈕 (僅在有訊息時顯示) */}
          {selectedCharacter && goal.trim() && messages.length > 0 && (
            <Button
              variant="outline-danger" // 改成紅色更醒目
              size="sm"
              onClick={handleClearChat}
              disabled={isLoading || isGettingFeedback}
              title={`清除與 ${selectedCharacter.name} 關於目標 "${goal}" 的聊天記錄`}
            >
              <i className="bi bi-trash3"></i> {/* 只留圖標更簡潔 */}
            </Button>
          )}
        </Col>

        {/* 錯誤提示 */}
        {error && ( /* ... 保持不變 ... */ <Col xs={12} className="mt-2"><Alert variant="danger" onClose={() => setError(null)} dismissible className="py-1 px-2 small mb-0">{error}</Alert></Col> )}
        {/* 添加未選擇角色/目標的提示 */}
        {(!selectedCharacter || !goal.trim()) && messages.length === 0 && !error && (
             <Col xs={12} className="mt-2">
                <Alert variant="info" className="text-center small p-2 mb-0">
                  請先選擇角色並設定目標，然後開始對話！
                </Alert>
            </Col>
        )}

      </Row>

      {/* --- 聊天顯示區域 --- */}
      {/* 使用 chat-display-row class */}
      <Row className="chat-display-row m-0">
        <Col className="p-0">
          <ChatDisplay messages={messages} isLoading={isLoading} />
        </Col>
      </Row>

      {/* --- 訊息輸入區域 --- */}
      {/* 使用 message-input-row class */}
      <Row className="message-input-row m-0">
        <Col>
          <MessageInput
            currentMessage={currentMessage}
            onMessageChange={setCurrentMessage}
            onSendMessage={handleSendMessage}
            onGetFeedback={handleGetFeedback}
            isLoading={isLoading}
            isGettingFeedback={isGettingFeedback}
            // 輸入框的 disabled 邏輯
            disabled={!selectedCharacter || !goal.trim() || isLoading || isGettingFeedback}
          />
        </Col>
      </Row>
    </Container>
  );
}

export default TrainingRoom;
